SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_prescr_proc_glic (nr_prescricao_p bigint, nr_seq_procedimento_p bigint, nr_seq_protocolo_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_glic_inic_w			double precision;
qt_glic_fim_w			double precision;
qt_ui_insulina_w		double precision;
qt_ui_insulina_int_w		double precision;
qt_glicose_w			real;
ds_sugestao_w			varchar(2000);
nr_seq_proc_glic_w		bigint;
qt_minutos_medicao_w	bigint;
ie_verifica_w			bigint;

/* obter niveis protocolo */

c01 CURSOR FOR
SELECT	qt_glic_inic,
	qt_glic_fim,
	qt_ui_insulina,
	qt_ui_insulina_int,
	qt_glicose,
	ds_sugestao,
	qt_minutos_medicao
from	pep_prot_glic_nivel
where	nr_seq_protocolo = nr_seq_protocolo_p
order by
	nr_sequencia;


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_procedimento_p IS NOT NULL AND nr_seq_procedimento_p::text <> '') and (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	/* limpar tabela x prescricao */

	delete
	from	prescr_proc_glic
	where	nr_prescricao = nr_prescricao_p
	and	nr_seq_procedimento = nr_seq_procedimento_p;

	/* gerar niveis protocolo */

	select	count(*)
	into STRICT	ie_verifica_w
	from	prescr_procedimento
	where	nr_prescricao = nr_prescricao_p
	and	nr_sequencia = nr_seq_procedimento_p;

	if (ie_verifica_w > 0) then
		open c01;
		loop
		fetch c01 into	qt_glic_inic_w,
				qt_glic_fim_w,
				qt_ui_insulina_w,
				qt_ui_insulina_int_w,
				qt_glicose_w,
				ds_sugestao_w,
				qt_minutos_medicao_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			select	nextval('prescr_proc_glic_seq')
			into STRICT	nr_seq_proc_glic_w
			;

			insert into prescr_proc_glic(
								nr_sequencia,
								nr_prescricao,
								nr_seq_procedimento,
								dt_atualizacao,
								nm_usuario,
								dt_atualizacao_nrec,
								nm_usuario_nrec,
								nr_seq_protocolo,
								qt_glic_inic,
								qt_glic_fim,
								qt_ui_insulina,
								qt_glicose,
								ds_sugestao,
								qt_minutos_medicao,
								qt_ui_insulina_int
								)
			values (
								nr_seq_proc_glic_w,
								nr_prescricao_p,
								nr_seq_procedimento_p,
								clock_timestamp(),
								nm_usuario_p,
								clock_timestamp(),
								nm_usuario_p,
								nr_seq_protocolo_p,
								qt_glic_inic_w,
								qt_glic_fim_w,
								qt_ui_insulina_w,
								qt_glicose_w,
								ds_sugestao_w,
								qt_minutos_medicao_w,
								qt_ui_insulina_int_w
								);
			end;
		end loop;
		close c01;
	end if;
end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
