CREATE OR REPLACE PROCEDURE gerar_prescr_protocolo_checkup ( cd_pessoa_fisica_p text, cd_medico_p text, cd_protocolo_p bigint, nr_sequencia_p bigint, cd_sub_tipo_p bigint, qt_superf_corp_p bigint, qt_peso_p bigint, nr_horas_validade_p bigint, nr_prescricao_p INOUT bigint, dt_procedimento_p timestamp, ds_dados_clinicos_p text, dt_primeiro_horario_p text, dt_prescricao_p timestamp, cd_intervalo_p text, qt_protocolo_p bigint, ie_adep_p text, nm_computador_p text, nr_atendimento_p bigint, ie_atendimento_p text, nm_usuario_p text) AS $body$
DECLARE

		 
nr_prescricao_w		bigint;
ie_atendimento_w	varchar(1) := 'N';
ie_fim_conta_w		varchar(1) := 'N';
nr_atendimento_w	bigint;


BEGIN 
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then 
	begin 
	ie_atendimento_w	:= ie_atendimento_p;
	nr_prescricao_w		:= nr_prescricao_p;
	 
	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and ('S' = ie_atendimento_w) then 
		begin 
		select	coalesce(ie_fim_conta,'F') 
		into STRICT	ie_fim_conta_w 
		from	atendimento_paciente 
		where	nr_atendimento = nr_atendimento_p;
		 
		if (ie_fim_conta_w <> 'F') then 
			begin 
			nr_atendimento_w := nr_atendimento_p;
			end;
		else 
			begin 
			nr_atendimento_w := null;
			ie_atendimento_w := 'N';
			end;
		end if;
		end;
	else 	 
		begin 
		nr_atendimento_w := null;
		end;
	end if;
	 
	nr_prescricao_w := gerar_prescricao_protocolo( 
		cd_pessoa_fisica_p, cd_medico_p, nr_atendimento_w, cd_protocolo_p, cd_sub_tipo_p, qt_superf_corp_p, qt_peso_p, nr_horas_validade_p, nr_prescricao_w, dt_procedimento_p, ds_dados_clinicos_p, dt_primeiro_horario_p, dt_prescricao_p, cd_intervalo_p, qt_protocolo_p, ie_adep_p, nm_usuario_p, nm_computador_p, null, null);
 
	update	checkup 
	set	nr_prescricao	= nr_prescricao_w 
	where	nr_sequencia	= nr_sequencia_p;
	 
	end;
end if;
nr_prescricao_p := nr_prescricao_w;
commit;
end;
