CREATE OR REPLACE PROCEDURE gerar_previsao_alta (nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_medico_resp_w	varchar(50);


BEGIN 
select	max(cd_medico_resp) 
into STRICT	cd_medico_resp_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p;
 
insert into atend_previsao_alta(nr_sequencia, 
				dt_atualizacao, 
				nm_usuario, 
				dt_registro, 
				cd_profissional, 
				nr_atendimento, 
				ie_situacao, 
				ie_alta_hospitalar, 
				nr_dias_prev_alta, 
				dt_previsto_alta, 
				ie_probabilidade_alta, 
				nr_seq_classif_medico, 
				ds_observacao, 
				dt_liberacao) 
			SELECT	nextval('atend_previsao_alta_seq'), 
				clock_timestamp(), 
				nm_usuario_p, 
				clock_timestamp(), 
				cd_medico_resp_w, 
				nr_atendimento_p, 
				'A', 
				ie_alta_hospitalar, 
				nr_dias_prev_alta, 
				dt_previsto_alta, 
				'C', 
				nr_seq_classif_medico, 
				ds_observacao, 
				clock_timestamp() 
			from	atend_previsao_alta 
			where	nr_sequencia = (SELECT 	max(nr_sequencia) 
						from 	atend_previsao_alta 
						where 	nr_atendimento = nr_atendimento_p 
						and 	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') 
						and	coalesce(dt_inativacao::text, '') = '');
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
