CREATE OR REPLACE PROCEDURE gerar_previsao_tributos ( nr_seq_nota_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ie_entrada_saida_w		varchar(1);					
dt_entrada_saida_w		timestamp;					
					 

BEGIN 
 
select	obter_se_nota_entrada_saida(nr_sequencia), 
	dt_entrada_saida 
into STRICT	ie_entrada_saida_w, 
	dt_entrada_saida_w 
from	nota_fiscal 
where	nr_sequencia = nr_seq_nota_p;
 
if (ie_entrada_saida_w = 'E') then 
	CALL gerar_tributos_fornecedor_item(nr_seq_nota_p, 0, null, 'N', dt_entrada_saida_w, nm_usuario_p, 'S');
	CALL gerar_tributos_fornecedor( nr_seq_nota_p,null, nm_usuario_p, dt_entrada_saida_w, 'S');
else 
	CALL gerar_imposto_nf_item(nr_seq_nota_p, 0, nm_usuario_p, 0, 0, 'S');
	CALL Gerar_Imposto_NF(nr_seq_nota_p, nm_usuario_p, 0, 0, 'S');
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
