C01 CURSOR FOR 
SELECT	nr_seq_solucao, 
		nr_seq_solucao, 
		ie_via_aplicacao, 
		cd_intervalo, 
		cd_unidade_medida, 
		ie_tipo_dosagem, 
		qt_dosagem, 
		qt_solucao_total, 
		qt_tempo_aplicacao, 
		ds_solucao, 
		CASE WHEN coalesce(ie_acm,'N')='S' THEN qt_volume  ELSE dividir(qt_solucao_total,nr_etapas) END , 
		nr_etapas, 
		ds_horarios, 
		ie_bomba_infusao, 
		nr_agrupamento, 
		ie_esquema_alternado, 
		coalesce(ie_calc_aut,'N'), 
		coalesce(ie_acm,'N'), 
		hr_prim_horario, 
		qt_hora_fase, 
		ds_orientacao, 
		ie_hemodialise, 
		ie_tipo_solucao, 
		ie_pos_dialisador, 
		nr_seq_protocolo, 
		ie_unid_vel_inf, 
		qt_temp_solucao, 
		ie_apap, 
		qt_dose_ataque, 
		ie_solucao_pca, 
		ie_tipo_analgesia, 
		ie_pca_modo_prog, 
		qt_vol_infusao_pca, 
		qt_bolus_pca, 
		qt_intervalo_bloqueio, 
		qt_limite_quatro_hora, 
		qt_dose_inicial_pca, 
		coalesce(ie_se_necessario,'N'), 
		ie_urgencia, 
		ie_etapa_especial 
from	prescr_solucao 
where	coalesce(ie_suspenso,'N') <> 'S' 
and		coalesce(ie_hemodialise,'N') = 'N' 
and		nr_prescricao = nr_prescricao_orig_p;

c03 CURSOR FOR 
SELECT a.nr_sequencia, 
		a.ie_origem_inf, 
		a.cd_material, 
		a.cd_unidade_medida, 
		a.qt_dose, 
		a.qt_unitaria, 
		a.qt_material, 
		a.cd_intervalo, 
		a.ds_horarios, 
		a.ds_observacao, 
		a.ds_observacao_enf, 
		a.ie_via_aplicacao, 
		a.nr_agrupamento, 
		coalesce(a.ie_cobra_paciente,'S'), 
		CASE WHEN coalesce(a.ie_regra_disp,'X')='D' THEN  a.cd_motivo_baixa  ELSE CASE WHEN coalesce(a.ie_cobra_paciente,'S')='S' THEN  0  ELSE a.cd_motivo_baixa END  END , 
		CASE WHEN coalesce(a.ie_regra_disp,'X')='D' THEN  clock_timestamp()  ELSE CASE WHEN coalesce(a.ie_cobra_paciente,'S')='S' THEN  null  ELSE clock_timestamp() END  END , 
		a.ie_utiliza_kit, 
		a.cd_unidade_medida_dose, 
		a.qt_conversao_dose, 
		coalesce(nr_etapas_w,a.nr_ocorrencia), 
		a.qt_total_dispensar, 
		a.cd_fornec_consignado, 
		CASE WHEN coalesce(a.nr_sequencia_proc::text, '') = '' THEN  null  ELSE a.nr_sequencia_proc END , 
		a.qt_solucao, 
		a.ds_dose_diferenciada, 
		a.ie_medicacao_paciente, 
		CASE WHEN coalesce(a.nr_sequencia_diluicao::text, '') = '' THEN  null  ELSE a.nr_sequencia_diluicao END , 
		a.HR_PRIM_HORARIO, 
		CASE WHEN coalesce(a.nr_sequencia_dieta::text, '') = '' THEN null  ELSE a.nr_sequencia_dieta END , 
		a.ie_agrupador, 
		a.nr_dia_util, 
		CASE WHEN b.ie_situacao='A' THEN  'N'  ELSE 'S' END , 
		a.ie_se_necessario, 
		a.qt_min_aplicacao, 
		a.ie_bomba_infusao, 
		coalesce(a.IE_APLIC_BOLUS,'N'), 
		coalesce(a.IE_APLIC_LENTA,'N'), 
		coalesce(a.ie_acm,'N'), 
		a.ie_objetivo, 
		a.cd_topografia_cih, 
		a.ie_origem_infeccao, 
		a.cd_amostra_cih, 
		a.cd_microorganismo_cih, 
		coalesce(a.ie_uso_antimicrobiano,'N'), 
		a.cd_protocolo, 
		a.nr_seq_protocolo, 
		a.nr_seq_mat_protocolo, 
		a.qt_hora_aplicacao, 
		A.QT_VEL_INFUSAO, 
		a.ds_justificativa, 
		a.ie_sem_aprazamento, 
		a.ie_indicacao, 
		a.dt_proxima_dose, 
		a.qt_total_dias_lib, 
		a.nr_seq_substituto, 
		a.ie_lado, 
		a.dt_inicio_medic, 
		a.qt_dia_prim_hor, 
		CASE WHEN coalesce(a.ie_regra_disp,'X')='D' THEN  a.ie_regra_disp  ELSE null END , 
		a.qt_vol_adic_reconst, 
		a.qt_hora_intervalo, 
		a.qt_min_intervalo, 
		a.qt_volume_corrigido, 
		a.ds_observacao_far 
From	Material b, 
		Prescr_Material a 
where	coalesce(a.ie_suspenso,'N') 		<> 'S' 
and		a.ie_origem_inf		<> 'K' 
and		((a.nr_sequencia <> a.nr_sequencia_diluicao) or (coalesce(a.nr_sequencia_diluicao::text, '') = '')) 
and		((coalesce(a.NR_SEQUENCIA_DILUICAO::text, '') = '') or 
		a.NR_SEQUENCIA_DILUICAO not in ( 
			SELECT	x.nr_sequencia 
			from	material y, 
					prescr_material x 
			where	y.cd_material = x.cd_material 
			and		y.ie_situacao <> 'A' 
			and		x.nr_prescricao = nr_prescricao_orig_p)) 
and		not exists (	select	nr_prescricao 
					from	prescr_dieta b 
					where	b.ie_suspenso		= 'S' 
					and		a.nr_sequencia_dieta = b.nr_sequencia 
					and		a.nr_prescricao	= b.nr_prescricao) 
and		not exists (	select	nr_prescricao 
					from	prescr_solucao c 
					where	c.ie_suspenso		= 'S' 
					and		a.nr_sequencia_solucao = c.nr_seq_solucao 
					and		a.nr_prescricao	= c.nr_prescricao) 
and		not exists (	select	nr_prescricao 
					from	prescr_procedimento d 
					where	d.ie_suspenso		= 'S' 
					and		a.nr_sequencia_proc	= d.nr_sequencia 
					and		a.nr_prescricao	= d.nr_prescricao) 
and		not exists (	select	nr_prescricao 
					from	prescr_material e 
					where	e.ie_suspenso		= 'S' 
					and		a.nr_sequencia_diluicao = e.nr_sequencia 
					and		a.nr_prescricao	= e.nr_prescricao) 
and		b.ie_situacao		= 'A' 
and		a.cd_material 	= b.cd_material 
and		a.ie_agrupador not in (10,11) 
and		a.ie_agrupador = 4 
and		a.nr_sequencia_solucao = nr_seq_solucao_filtro_w 
and		a.nr_prescricao 	= nr_prescricao_orig_p;


BEGIN 
 
select	coalesce(max(cd_estabelecimento),1), 
		max(cd_setor_atendimento), 
		max(nr_atendimento), 
		max(nr_horas_validade) 
into STRICT	cd_estabelecimento_w, 
		cd_setor_atendimento_w, 
		nr_atendimento_w, 
		nr_horas_validade_orig_w 
from	prescr_medica 
where	nr_prescricao = nr_prescricao_orig_p;
 
select	max(dt_inicio_prescr), 
		max(dt_validade_prescr), 
		coalesce(max(nr_horas_validade),24) 
into STRICT	dt_inicio_prescr_w, 
		dt_validade_prescr_w, 
		nr_horas_validade_w 
from	prescr_medica 
where	nr_prescricao = nr_prescricao_p;
 
ie_recalcular_hor_sol_w := Obter_Param_Usuario(8030, 64, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_recalcular_hor_sol_w);
VarIeGeracaoEtapaACM_w := Obter_Param_Usuario(924, 226, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, VarIeGeracaoEtapaACM_w);
VarHorarioSolucao_w := Obter_Param_Usuario(924, 284, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, VarHorarioSolucao_w);
ie_recalc_total_w := Obter_Param_Usuario(924, 322, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_recalc_total_w);
VarCalculaTempoSolucao_w := Obter_Param_Usuario(924, 394, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, VarCalculaTempoSolucao_w);
VarCalcularEtapaSolucao_w := Obter_Param_Usuario(924, 461, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, VarCalcularEtapaSolucao_w);
VarCalculaVolumeVelocidade_w := Obter_Param_Usuario(924, 636, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, VarCalculaVolumeVelocidade_w);
ie_arredondar_etapa_w := Obter_Param_Usuario(924, 742, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_arredondar_etapa_w);
ie_prim_hor_sol_w := Obter_Param_Usuario(924, 332, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_prim_hor_sol_w);
ie_prim_hor_copia_w := Obter_Param_Usuario(924, 790, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_prim_hor_copia_w);
 
open C01;
loop 
fetch C01 into 
	nr_seq_solucao_w, 
	nr_seq_solucao_filtro_w, 
	ie_via_aplicacao_w, 
	cd_intervalo_w, 
	cd_unidade_medida_w, 
	ie_tipo_dosagem_w, 
	qt_dosagem_w, 
	qt_solucao_total_w, 
	qt_tempo_aplicacao_w, 
	ds_solucao_w, 
	qt_volume_w, 
	nr_etapas_w, 
	ds_horarios_w, 
	ie_bomba_infusao_w, 
	nr_agrupamento_w, 
	ie_esquema_alternado_w, 
	ie_calc_aut_w, 
	ie_acm_w, 
	hr_prim_horario_w, 
	qt_hora_fase_w, 
	ds_orientacao_w, 
	ie_hemodialise_w, 
	ie_tipo_solucao_w, 
	ie_pos_dialisador_w, 
	nr_seq_protocolo_w, 
	ie_unid_vel_inf_w, 
	qt_temp_solucao_w, 
	ie_apap_w, 
	qt_dose_ataque_w, 
	ie_solucao_pca_w, 
	ie_tipo_analgesia_w, 
	ie_pca_modo_prog_w, 
	qt_vol_infusao_pca_w, 
	qt_bolus_pca_W, 
	qt_intervalo_bloqueio_w, 
	qt_limite_quatro_hora_w, 
	qt_dose_inicial_pca_w, 
	ie_se_necessario_w, 
	ie_agora_w, 
	ie_etapa_especial_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
 
	hr_prim_horario_orig_w	:= hr_prim_horario_w;
	qt_operacao_orig_w	:= null;
 
	if (ie_recalcular_hor_sol_w = 'S') and (ie_acm_w = 'N') then 
		hr_prim_horario_w := to_char(dt_inicio_prescr_w,'hh24:mi');
	end if;
 
	if (coalesce(ie_manter_intervalo_w,'N') = 'S') then 
		qt_operacao_orig_w	:= dividir(coalesce(qt_tempo_aplicacao_w, coalesce(nr_horas_validade_orig_w, 24)), coalesce(nr_etapas_w,0));
	end if;
 
	qt_tempo_aplicacao_w	:= nr_horas_validade_w;
 
	ie_loop_w	:= 'S';
	nr_seq_solucao_w	:= 1;
 
	while(ie_loop_w = 'S') loop 
 
		select	count(*) 
		into STRICT	qt_itens_w 
		from	prescr_solucao 
		where	nr_prescricao	= nr_prescricao_p 
		and		nr_seq_solucao	= nr_seq_solucao_w;
 
		if (qt_itens_w	= 0) then 
			ie_loop_w	:= 'N';
		else 
			nr_seq_solucao_w	:= nr_seq_solucao_w + 1;
		end if;
 
	end loop;
 
	--Busca o próximo agrupamento 
	ie_loop_w	:= 'S';
	nr_agrupamento_w	:= 1;
 
	while(ie_loop_w = 'S') loop 
 
		select	count(*) 
		into STRICT	qt_itens_w 
		from	prescr_solucao 
		where	nr_prescricao	= nr_prescricao_p 
		and		nr_agrupamento	= nr_agrupamento_w;
 
		if (qt_itens_w	= 0) then 
			ie_loop_w	:= 'N';
		else 
			nr_agrupamento_w	:= nr_agrupamento_w + 1;
		end if;
 
	end loop;
 
	ds_horarios_w := to_char(clock_timestamp(), 'hh24:mi');
 
	Insert into Prescr_Solucao( 
			nr_prescricao, 
			nr_seq_solucao, 
			ie_via_aplicacao, 
			cd_intervalo, 
			dt_atualizacao, 
			nm_usuario, 
			cd_unidade_medida, 
			ie_tipo_dosagem, 
			qt_dosagem, 
			qt_solucao_total, 
			qt_tempo_aplicacao, 
			ds_solucao, 
			qt_volume, 
			nr_etapas, 
			ds_horarios, 
			ie_bomba_infusao, 
			ie_suspenso, 
			nr_agrupamento, 
			ie_esquema_alternado, 
			ie_calc_aut, 
			ie_acm, 
			hr_prim_horario, 
			qt_hora_fase, 
			ie_urgencia, 
			ds_orientacao, 
			ie_hemodialise, 
			ie_tipo_solucao, 
			ie_pos_dialisador, 
			nr_seq_protocolo, 
			ie_unid_vel_inf, 
			qt_temp_solucao, 
			ie_apap, 
			qt_dose_ataque, 
			ie_solucao_pca, 
			ie_tipo_analgesia, 
			ie_pca_modo_prog, 
			qt_vol_infusao_pca, 
			qt_bolus_pca, 
			qt_intervalo_bloqueio, 
			qt_limite_quatro_hora, 
			qt_dose_inicial_pca, 
			ie_etapa_especial, 
			ie_se_necessario, 
			nr_seq_anterior, 
			nr_prescricao_original, 
			nr_prescricao_anterior, 
			nr_sequencia_anterior) 
		values ( 
			nr_prescricao_p, 
			nr_seq_solucao_w, 
			ie_via_aplicacao_w, 
			cd_intervalo_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			cd_unidade_medida_w, 
			ie_tipo_dosagem_w, 
			qt_dosagem_w, 
			qt_solucao_total_w, 
			qt_tempo_aplicacao_w, 
			ds_solucao_w, 
			qt_volume_w, 
			nr_etapas_w, 
			ds_horarios_w, 
			ie_bomba_infusao_w, 
			'N', 
			nr_agrupamento_w, 
			ie_esquema_alternado_w, 
			ie_calc_aut_w, 
			ie_acm_w, 
			hr_prim_horario_w, 
			qt_hora_fase_w, 
			'S', 
			ds_orientacao_w, 
			ie_hemodialise_w, 
			ie_tipo_solucao_w, 
			ie_pos_dialisador_w, 
			nr_seq_protocolo_w, 
			ie_unid_vel_inf_w, 
			qt_temp_solucao_w, 
			ie_apap_w, 
			qt_dose_ataque_w, 
			ie_solucao_pca_w, 
			ie_tipo_analgesia_w, 
			ie_pca_modo_prog_w, 
			qt_vol_infusao_pca_w, 
			qt_bolus_pca_w, 
			qt_intervalo_bloqueio_w, 
			qt_limite_quatro_hora_w, 
			qt_dose_inicial_pca_w, 
			'N', 
			ie_se_necessario_w, 
			nr_seq_solucao_filtro_w, 
			nr_prescricao_orig_p, 
			nr_prescricao_orig_p, 
			nr_seq_solucao_filtro_w);
 
	commit;
 
	insert into prescr_solucao_esquema( 
			nr_sequencia, 
			nr_prescricao, 
			nr_seq_solucao, 
			dt_atualizacao, 
			nm_usuario, 
			qt_volume, 
			qt_dosagem, 
			ds_horario) 
	SELECT	nextval('prescr_solucao_esquema_seq'), 
			nr_prescricao_p, 
			nr_seq_solucao_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			b.qt_volume, 
			b.qt_dosagem, 
			b.ds_horario 
	from	prescr_solucao_esquema b, 
			prescr_solucao a 
	where	coalesce(a.ie_suspenso,'N')	<> 'S' 
	and		a.nr_seq_solucao = b.nr_seq_solucao 
	and		a.nr_prescricao	= b.nr_prescricao 
	and		a.nr_seq_solucao = nr_seq_solucao_w 
	and		a.nr_prescricao = nr_prescricao_orig_p;
 
	commit;
 
	nr_seq_material_w	:= 1;
 
	open C03;
	loop 
	fetch C03 into 
		nr_sequencia_w, 
		ie_origem_inf_w, 
		cd_material_w, 
		cd_unidade_medida_w, 
		qt_dose_w, 
		qt_unitaria_w, 
		qt_material_w, 
		cd_intervalo_w, 
		ds_horarios_w, 
		ds_observacao_w, 
		ds_observacao_enf_w, 
		ie_via_aplicacao_w, 
		nr_agrupamento_w, 
		ie_cobra_paciente_w, 
		cd_motivo_baixa_w, 
		dt_baixa_w, 
		ie_utiliza_kit_w, 
		cd_unidade_medida_dose_w, 
		qt_conversao_dose_w, 
		nr_ocorrencia_w, 
		qt_total_dispensar_w, 
		cd_fornec_consignado_w, 
		nr_sequencia_proc_w, 
		qt_solucao_w, 
		ds_dose_diferenciada_w, 
		ie_medicacao_paciente_w, 
		nr_sequencia_diluicao_w, 
		HR_PRIM_HORARIO_w, 
		nr_sequencia_dieta_w, 
		ie_agrupador_w, 
		nr_dia_util_w, 
		ie_situacao_w, 
		ie_se_necessario_w, 
		qt_min_aplicacao_w, 
		ie_bomba_infusao_w, 
		IE_APLIC_BOLUS_w, 
		IE_APLIC_LENTA_w, 
		ie_acm_w, 
		ie_objetivo_w, 
		cd_topografia_cih_w, 
		ie_origem_infeccao_w, 
		cd_amostra_cih_w, 
		cd_microorganismo_cih_w, 
		ie_uso_antimicrobiano_w, 
		cd_protocolo_w, 
		nr_seq_protocolo_w, 
		nr_seq_mat_protocolo_w, 
		qt_hora_aplicacao_w, 
		QT_VEL_INFUSAO_w, 
		ds_justificativa_w, 
		ie_sem_aprazamento_w, 
		ie_indicacao_w, 
		dt_proxima_dose_w, 
		qt_total_dias_lib_w, 
		nr_seq_substituto_w, 
		ie_lado_w, 
		dt_inicio_medic_w, 
		qt_dia_prim_hor_w, 
		ie_regra_disp_w, 
		qt_vol_adic_reconst_w, 
		qt_hora_intervalo_w, 
		qt_min_intervalo_w, 
		qt_volume_corrigido_w, 
		ds_observacao_far_w;
	EXIT WHEN NOT FOUND; /* apply on C03 */
 
		ie_loop_w	:= 'S';
		nr_agrupamento_w		:= 1;
 
		while(ie_loop_w = 'S') loop 
 
			select	count(*) 
			into STRICT	qt_itens_w 
			from	prescr_solucao 
			where	nr_agrupamento	= nr_agrupamento_w 
			and		nr_prescricao	= nr_prescricao_p;
			if (qt_itens_w	= 0) then 
				ie_loop_w	:= 'N';
			else 
				nr_agrupamento_w	:= nr_agrupamento_w + 1;
			end if;
 
		end loop;
 
		ie_loop_w	:= 'S';
 
		while(ie_loop_w = 'S') loop 
 
			select	count(*) 
			into STRICT	qt_itens_w 
			from	prescr_material 
			where	nr_sequencia	= coalesce(nr_sequencia_w,0) + coalesce(nr_seq_material_w,0) 
			and		nr_prescricao	= nr_prescricao_p;
 
			if (qt_itens_w	= 0) then 
				ie_loop_w	:= 'N';
			else 
				nr_seq_material_w	:= coalesce(nr_seq_material_w,0) + 1;
			end if;
 
		end loop;
 
		Insert into Prescr_Material( 
			nr_prescricao, 
			nr_sequencia, 
			ie_origem_inf, 
			cd_material, 
			cd_unidade_medida, 
			qt_dose, 
			qt_unitaria, 
			qt_material, 
			dt_atualizacao, 
			nm_usuario, 
			cd_intervalo, 
			ds_horarios, 
			ds_observacao, 
			ds_observacao_enf, 
			ie_via_aplicacao, 
			nr_agrupamento, 
			ie_cobra_paciente, 
			cd_motivo_baixa, 
			dt_baixa, 
			ie_utiliza_kit, 
			cd_unidade_medida_dose, 
			qt_conversao_dose, 
			ie_urgencia, 
			nr_ocorrencia, 
			qt_total_dispensar, 
			cd_fornec_consignado, 
			nr_sequencia_solucao, 
			nr_sequencia_proc, 
			qt_solucao, 
			hr_dose_especial, 
			qt_dose_especial, 
			ds_dose_diferenciada, 
			ie_medicacao_paciente, 
			nr_sequencia_diluicao, 
			hr_prim_horario, 
			nr_sequencia_dieta, 
			ie_agrupador, 
			nr_dia_util, 
			ie_suspenso, 
			ie_se_necessario, 
			qt_min_aplicacao, 
			ie_bomba_infusao, 
			ie_aplic_bolus, 
			ie_aplic_lenta, 
			ie_acm, 
			ie_objetivo, 
			cd_topografia_cih, 
			ie_origem_infeccao, 
			cd_amostra_cih, 
			cd_microorganismo_cih, 
			ie_uso_antimicrobiano, 
			cd_protocolo, 
			nr_seq_protocolo, 
			nr_seq_mat_protocolo, 
			qt_hora_aplicacao, 
			ie_recons_diluente_fixo, 
			qt_vel_infusao, 
			ds_justificativa, 
			ie_sem_aprazamento, 
			ie_indicacao, 
			dt_proxima_dose, 
			qt_total_dias_lib, 
			nr_seq_substituto, 
			ie_lado, 
			dt_inicio_medic, 
			qt_dia_prim_hor, 
			ie_regra_disp, 
			qt_vol_adic_reconst, 
			qt_hora_intervalo, 
			qt_min_intervalo, 
			nr_seq_anterior, 
			nr_prescricao_original, 
			qt_volume_corrigido, 
			ds_observacao_far) 
		values (nr_prescricao_p, 
				coalesce(nr_sequencia_w,0) + coalesce(nr_seq_material_w,0), 
			ie_origem_inf_w, 
			cd_material_w, 
			cd_unidade_medida_w, 
			qt_dose_w, 
			qt_unitaria_w, 
			qt_material_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			substr(cd_intervalo_w,1,7), 
			substr(ds_horarios_w,1,2000), 
			substr(ds_observacao_w,1,4000), 
			substr(ds_observacao_enf_w,1,2000), 
			ie_via_aplicacao_w, 
			nr_agrupamento_w, 
			ie_cobra_paciente_w, 
			cd_motivo_baixa_w, 
			dt_baixa_w, 
			ie_utiliza_kit_w, 
			cd_unidade_medida_dose_w, 
			qt_conversao_dose_w, 
			'N', 
			nr_ocorrencia_w, 
			qt_total_dispensar_w, 
			cd_fornec_consignado_w, 
			nr_seq_solucao_w, 
			nr_sequencia_proc_w, 
			qt_solucao_w, 
			null, 
			null, 
			substr(ds_dose_diferenciada_w,1,255), 
			ie_medicacao_paciente_w, 
			null, 
			hr_prim_horario_w, 
			nr_sequencia_dieta_w, 
			ie_agrupador_w, 
			nr_dia_util_w, 
			ie_situacao_w, 
			ie_se_necessario_w, 
			qt_min_aplicacao_w, 
			ie_bomba_infusao_w, 
			ie_aplic_bolus_w, 
			ie_aplic_lenta_w, 
			ie_acm_w, 
			ie_objetivo_w, 
			cd_topografia_cih_w, 
			ie_origem_infeccao_w, 
			cd_amostra_cih_w, 
			cd_microorganismo_cih_w, 
			ie_uso_antimicrobiano_w, 
			cd_protocolo_w, 
			nr_seq_protocolo_w, 
			nr_seq_mat_protocolo_w, 
			qt_hora_aplicacao_w, 
			'N', 
			QT_VEL_INFUSAO_w, 
			substr(ds_justificativa_w,1,2000), 
			ie_sem_aprazamento_w, 
			ie_indicacao_w, 
			dt_proxima_dose_w, 
			qt_total_dias_lib_w, 
			nr_seq_substituto_w, 
			ie_lado_w, 
			dt_inicio_medic_w, 
			qt_dia_prim_hor_w, 
			ie_regra_disp_w, 
			qt_vol_adic_reconst_w, 
			qt_hora_intervalo_w, 
			qt_min_intervalo_w, 
			nr_sequencia_w, 
			nr_prescricao_orig_p, 
			qt_volume_corrigido_w, 
			ds_observacao_far_w);
 
		nr_seq_disp_w := coalesce(nr_sequencia_w,0) + coalesce(nr_seq_material_w,0);
 
		SELECT * FROM Obter_Quant_Dispensar(	cd_estabelecimento_w, cd_material_w, nr_prescricao_p, nr_seq_disp_w, cd_intervalo_w, ie_via_aplicacao_w, qt_unitaria_w, null, nr_ocorrencia_w, ds_dose_diferenciada_w, ie_origem_inf_w, cd_unidade_medida_dose_w, 1, qt_material_w, qt_total_dispensar_w, ie_regra_disp_w, ds_erro_w, ie_se_necessario_w, ie_acm_w) INTO STRICT qt_material_w, qt_total_dispensar_w, ie_regra_disp_w, ds_erro_w;
 
		update	prescr_material 
		set 	nr_ocorrencia		= nr_ocorrencia_w, 
				qt_total_dispensar	= qt_total_dispensar_w, 
				qt_material			= coalesce(qt_material_w,1), 
				ie_regra_disp		= CASE WHEN coalesce(ie_regra_disp,'X')='D' THEN  ie_regra_disp  ELSE ie_regra_disp_w END  
		where	nr_sequencia		= nr_seq_disp_w 
		and		nr_prescricao 		= nr_prescricao_p;
 
	end loop;
	close C03;
 
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_pre_rep_sol ( nr_prescricao_orig_p bigint, nr_prescricao_p bigint, nm_usuario_p text) FROM PUBLIC;

