SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_prim_checagem_ccg ( nr_sequencia_p bigint, nm_usuario_p text, ie_desfazer_p text default 'N') AS $body$
DECLARE

									
nr_seq_evento_w		atend_glicemia_evento.nr_sequencia%type;

							

BEGIN
if (ie_desfazer_p = 'S') then
	update	atendimento_glicemia
	set	dt_primeira_checagem  = NULL
	where	nr_sequencia = nr_sequencia_p;
	
	select 	max(nr_sequencia)
	into STRICT	nr_seq_evento_w
	from 	atend_glicemia_evento
	where 	ie_evento = 13
	and 	nr_seq_glicemia = nr_sequencia_p
	and 	nm_usuario = nm_usuario_p;
	
	delete 	FROM atend_glicemia_evento
	where 	nr_sequencia = nr_seq_evento_w;
else
	update	atendimento_glicemia
	set	dt_primeira_checagem = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_sequencia_p;

	select	nextval('atend_glicemia_evento_seq')
	into STRICT	nr_seq_evento_w
	;

	insert into atend_glicemia_evento(
		nr_sequencia,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_atualizacao,
		nm_usuario,
		nr_seq_glicemia,
		ie_evento,
		dt_evento,
		cd_pessoa_evento,
		ds_observacao,
		ie_local_aplicacao)
	values (
		nr_seq_evento_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_sequencia_p,
		13,
		clock_timestamp(),
		substr(obter_dados_usuario_opcao(nm_usuario_p,'C'),1,10),
		null,
		null);
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
