CREATE OR REPLACE PROCEDURE gerar_privilegio_pf ( cd_pessoa_fisica_p text, ds_valores_p text, nm_usuario_p text) AS $body$
DECLARE


ds_lista_valores_w				varchar(4000);
ie_pos_virgula_w				smallint	:= 0;
tam_lista_w					bigint;
nr_seq_privilegio_w				bigint;



BEGIN
ds_lista_valores_w	:= substr( ds_valores_p ,1,4000);

if (ds_valores_p IS NOT NULL AND ds_valores_p::text <> '') then
	begin
	while(ds_lista_valores_w IS NOT NULL AND ds_lista_valores_w::text <> '') loop
		begin
		tam_lista_w	:= length(ds_lista_valores_w);
		ie_pos_virgula_w	:= position(',' in ds_lista_valores_w );

		if (ie_pos_virgula_w <> 0) then
			nr_seq_privilegio_w	:= substr(ds_lista_valores_w,1,( ie_pos_virgula_w - 1));
			ds_lista_valores_w	:= substr(ds_lista_valores_w,(ie_pos_virgula_w + 1), tam_lista_w);
		end if;

			insert into pessoa_fisica_privilegio(
					nr_sequencia,
					cd_pessoa_fisica,
					nr_seq_privilegio,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec)
			values (		nextval('pessoa_fisica_privilegio_seq'),
					cd_pessoa_fisica_p,
					nr_seq_privilegio_w,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p);
		end;
	end loop;
	end;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
