CREATE OR REPLACE PROCEDURE gerar_procedimento_rotina ( nr_cirurgia_p bigint, nr_seq_proc_interno_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

										 
nr_sequencia_w		bigint;										
cd_convenio_w		bigint;		
cd_procedimento_w	bigint;		
ie_origem_proced_w	bigint;		
dt_prescricao_w		timestamp;				
nr_prescricao_w		bigint;


BEGIN 
 
select	cd_convenio, 
	nr_prescricao 
into STRICT	cd_convenio_w, 
	nr_prescricao_w 
from	cirurgia 
where	nr_cirurgia = nr_cirurgia_p;
 
SELECT * FROM Obter_Proc_Tab_Interno(nr_seq_proc_interno_p, nr_prescricao_w, null, null, cd_procedimento_w, ie_origem_proced_w, null, null) INTO STRICT cd_procedimento_w, ie_origem_proced_w;
 
select	dt_prescricao 
into STRICT 	dt_prescricao_w 
from 	prescr_medica 
where 	nr_prescricao = nr_prescricao_w;
 
 
select	coalesce(max(nr_sequencia), 0) + 1 
into STRICT	nr_sequencia_w 
from	prescr_procedimento 
where	nr_prescricao = nr_prescricao_w;
 
select	dt_prescricao 
into STRICT 	dt_prescricao_w 
from 	prescr_medica 
where 	nr_prescricao = nr_prescricao_w;
 
insert into prescr_procedimento( 
			nr_prescricao, 
			nr_sequencia, 
			nr_seq_proc_interno, 
			cd_procedimento, 
			ie_origem_proced, 
			cd_medico_exec, 
			qt_procedimento, 
			ie_urgencia, 
			ie_suspenso, 
			dt_prev_execucao, 
			ie_status_atend, 
			dt_atualizacao, 
			nm_usuario, 
			ie_origem_inf, 
			nr_seq_interno, 
			ie_avisar_result, 
			cd_motivo_baixa) 
values (			nr_prescricao_w, 
			nr_sequencia_w, 
			nr_seq_proc_interno_p, 
			cd_procedimento_w, 
			ie_origem_proced_w, 
			null, 
			1, 
			'N', 
			'N', 
			dt_prescricao_w, 
			5, 
			clock_timestamp(), 
			nm_usuario_p, 
			'1', 
			nextval('prescr_procedimento_seq'), 
			'N', 
			0);	
commit;			
										 
end;
$body$
