SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_proced_cobranca_taxa ( nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_atendimento_w			atend_cobranca_taxa.nr_atendimento%type;
nr_seq_proc_interno_w		regra_cobranca_taxa.nr_seq_proc_interno%type;
cd_convenio_w			atend_categoria_convenio.cd_convenio%type;
cd_categoria_w			atend_categoria_convenio.cd_categoria%type;
cd_setor_atendimento_w		atend_paciente_unidade.cd_setor_atendimento%type;
cd_procedimento_w		procedimento_paciente.cd_procedimento%type;
ie_origem_proced_w		procedimento_paciente.ie_origem_proced%type;
nr_seq_atepacu_w			procedimento_paciente.nr_seq_atepacu%type;
cd_estabelecimento_w		atendimento_paciente.cd_estabelecimento%type;
nr_sequencia_w			procedimento_paciente.nr_sequencia%type;
ds_erro_w			varchar(1)	:= '';

c01 CURSOR FOR
	SELECT	a.nr_atendimento,
		b.cd_estabelecimento
	from	atend_cobranca_taxa a,
		atendimento_paciente b
	where	a.nr_atendimento = b.nr_atendimento
	and	coalesce(a.dt_fim_cobranca::text, '') = ''
	and	coalesce(b.dt_alta::text, '') = ''
	and	((coalesce(nr_atendimento_p::text, '') = '') or (a.nr_atendimento = nr_atendimento_p))
	order by 1;

c02 CURSOR FOR
	SELECT	nr_seq_proc_interno
	from	regra_cobranca_taxa
	where	ie_situacao = 'A'
	and	coalesce(cd_convenio, cd_convenio_w) = cd_convenio_w
	and	coalesce(cd_categoria, cd_categoria_w) = cd_categoria_w
	and	coalesce(cd_setor_atendimento, coalesce(cd_setor_atendimento_w, 0)) = coalesce(cd_setor_atendimento_w, 0)
	order by  coalesce(cd_convenio,0),
		coalesce(cd_categoria,0),
		coalesce(cd_setor_atendimento,0);


BEGIN

open c01;
loop
fetch c01 into
	nr_atendimento_w,
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	coalesce(max(obter_convenio_atendimento(nr_atendimento_w)),0),
		coalesce(max(obter_categoria_atendimento(nr_atendimento_w)),'0')
	into STRICT	cd_convenio_w,
		cd_categoria_w
	;

	nr_seq_atepacu_w := obter_atepacu_paciente(nr_atendimento_w, 'A');

	select 	max(cd_setor_atendimento)
	into STRICT	cd_setor_atendimento_w
	from 	atend_paciente_unidade
	where 	nr_seq_interno = nr_seq_atepacu_w;

	open c02;
	loop
	fetch c02 into
		nr_seq_proc_interno_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		nr_seq_proc_interno_w := nr_seq_proc_interno_w;
		end;
	end loop;
	close c02;

	if (nr_seq_proc_interno_w IS NOT NULL AND nr_seq_proc_interno_w::text <> '') then
		begin

		SELECT * FROM Obter_Proc_Tab_Interno_Conv(nr_seq_proc_interno_w, cd_estabelecimento_w, cd_convenio_w, cd_categoria_w, null, cd_setor_atendimento_w, cd_procedimento_w, ie_origem_proced_w, null, clock_timestamp(), 0, null, null, null, 0, 0, 0, null) INTO STRICT cd_procedimento_w, ie_origem_proced_w;

		nr_sequencia_w := inserir_procedimento_pac(
			cd_procedimento_w, 1, null, nr_seq_proc_interno_w, ie_origem_proced_w, cd_setor_atendimento_w, nr_atendimento_w, cd_estabelecimento_w, nm_usuario_p, 'N', null, nr_seq_atepacu_w, clock_timestamp(), cd_convenio_w, cd_categoria_w, null, nr_sequencia_w);

		CALL atualiza_preco_procedimento(nr_sequencia_w, cd_convenio_w, nm_usuario_p);

		update procedimento_paciente
		--set    ds_observacao = 'Gerado pela Regra cobran√ßa de taxa de uso'
		set    ds_observacao = wheb_mensagem_pck.get_texto(303977)
		where  nr_sequencia = nr_sequencia_w;

		nr_sequencia_w := null;
		nr_seq_proc_interno_w := null;
		end;
	end if;

	exception
	when others then
		ds_erro_w := 'S';
	end;
end loop;
close c01;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_proced_cobranca_taxa ( nr_atendimento_p bigint, nm_usuario_p text) FROM PUBLIC;

