CREATE OR REPLACE PROCEDURE gerar_proced_coparticipacao ( nr_seq_autorizacao_p bigint, vl_coparticipacao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_atendimento_w	bigint;	
cd_convenio_w		integer;	
cd_categoria_w		varchar(20);
cd_procedimento_w	bigint;	
ie_origem_proced_w	integer;
nr_seq_atepacu_w	bigint;
cd_setor_atendimento_w	bigint;
nr_sequencia_w		bigint;
vl_coparticipacao_w	double precision;
ie_tipo_atendimento_w	bigint;

vl_inteiro_w		bigint;
vl_decimais_w		double precision;

ie_medico_executor_w	varchar(20);
cd_cgc_regra_w 		varchar(20);
cd_medico_regra_w 	varchar(20);
cd_profissional_w	varchar(20);
		

BEGIN 
 
 
vl_coparticipacao_w 	:= somente_numero(vl_coparticipacao_p);
vl_inteiro_w		:= to_number(substr(vl_coparticipacao_w,1,(length(vl_coparticipacao_w)-2)));
vl_decimais_w		:= to_number(substr(vl_coparticipacao_w,(length(vl_coparticipacao_w)-1),length(vl_coparticipacao_w)));
vl_decimais_w		:= dividir(vl_decimais_w,100);
vl_coparticipacao_w 	:= vl_inteiro_w + vl_decimais_w;
 
begin 
 
select 	coalesce(max(nr_atendimento),0) 
into STRICT	nr_atendimento_w 
from	autorizacao_convenio 
where	nr_sequencia = nr_seq_autorizacao_p;
exception 
when others then 
	nr_atendimento_w:=0;
end;
 
if (nr_atendimento_w > 0) and (vl_coparticipacao_w > 0) then 
 
	nr_seq_atepacu_w := Obter_Atepacu_paciente(nr_atendimento_W,'A');
	 
	 
	select	coalesce(max(ie_tipo_atendimento),0) 
	into STRICT	ie_tipo_atendimento_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_w;
		 
	select	coalesce(max(cd_setor_atendimento),0) 
	into STRICT	cd_setor_atendimento_w 
	from	atend_paciente_unidade 
	where	nr_seq_interno = nr_seq_atepacu_w;
	 
 
	begin 
	select	coalesce(max(cd_convenio),0), 
		coalesce(max(cd_categoria),'0') 
	into STRICT	cd_convenio_w, 
		cd_categoria_w 
	from 	atend_categoria_convenio a 
	where 	a.nr_atendimento		= nr_atendimento_w 
	and	a.dt_inicio_vigencia	= (	SELECT	max(dt_inicio_vigencia) 
						from	atend_categoria_convenio b 
						where 	nr_atendimento	= nr_atendimento_w);	
	exception 
	when others then 
		cd_convenio_w := 0;
		cd_categoria_w:= '0';
	end;	
			 
			 
	if (cd_convenio_w	> 0) and (cd_categoria_w	<> '0') and (cd_setor_atendimento_w	> 0) then				 
		 
		select	coalesce(max(cd_procedimento),0), 
			coalesce(max(ie_origem_proced),0) 
		into STRICT	cd_procedimento_w, 
			ie_origem_proced_w			 
		from	conv_taxa_copartic 
		where	cd_convenio = cd_convenio_w;
		 
		SELECT * FROM consiste_medico_executor(cd_estabelecimento_p, cd_convenio_w, cd_setor_atendimento_w, cd_procedimento_w, ie_origem_proced_w, ie_tipo_atendimento_w, null, null, ie_medico_executor_w, cd_cgc_regra_w, cd_medico_regra_w, cd_profissional_w, null, clock_timestamp(), null, 'N', null, null) INTO STRICT ie_medico_executor_w, cd_cgc_regra_w, cd_medico_regra_w, cd_profissional_w;
		 
		 
		if (ie_medico_executor_w = 'A') then 
			select	max(cd_medico_resp) 
			into STRICT	cd_medico_regra_w 
			from	atendimento_paciente 
			where	nr_atendimento = nr_atendimento_w;
		end if;
					 
		 
		if (cd_procedimento_w > 0) and (ie_origem_proced_w > 0) then 
			 
			nr_sequencia_w := inserir_proc_pac(	cd_procedimento_w, 1, null, null, ie_origem_proced_w, cd_setor_atendimento_w, nr_atendimento_w, cd_estabelecimento_p, nm_usuario_p, null, 'S', cd_medico_regra_w, nr_seq_atepacu_w, clock_timestamp(), cd_convenio_w, cd_categoria_w, nr_sequencia_w, vl_coparticipacao_w);	
		end if;
	end if;				
end if;
 
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
