SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_proc_pac_sus_aih ( nr_atendimento_p bigint, NR_INTERNO_CONTA_P bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, qt_procedimento_p bigint, nr_nota_fiscal_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w			bigint;
nr_seq_interno_w		bigint;
cd_convenio_w			integer;
cd_categoria_w			varchar(10);
cd_cgc_prestador_w		varchar(14);
cd_setor_atendimento_w		integer;
cd_atividade_profissional_w	smallint;
ie_tipo_atend_bpa_w		smallint;
ie_grupo_atend_bpa_w		smallint;
ie_tipo_servico_sus_w		smallint;
ie_tipo_ato_sus_w		smallint;
dt_entrada_unidade_w		timestamp;
ds_erro_w			varchar(255);
nr_nf_prestador_w		varchar(255);
cd_estabelecimento_w		smallint;

 

BEGIN 
if (qt_procedimento_p > 0) then 
	begin 
	nr_nf_prestador_w := nr_nota_fiscal_p;
	if (nr_nota_fiscal_p = 0) then 
		nr_nf_prestador_w := null;
	end if;
 
	 
	begin 
	select	b.cd_cgc, 
		a.cd_estabelecimento 
	into STRICT	cd_cgc_prestador_w, 
		cd_estabelecimento_w 
	from	estabelecimento b, 
		atendimento_paciente a 
	where	a.nr_atendimento	= nr_atendimento_p 
	and	a.cd_estabelecimento	= b.cd_estabelecimento;
	exception 
		when others then 
			cd_cgc_prestador_w := '';
	end;
 
 
	begin 
	select	cd_convenio, 
		cd_categoria 
	into STRICT	cd_convenio_w, 
		cd_categoria_w 
	from 	Atend_categoria_convenio a 
	where	a.nr_atendimento		= nr_atendimento_p 
	and	a.nr_seq_interno		= obter_atecaco_atendimento(nr_atendimento_p);
	exception 
		when others then 
			cd_convenio_w	:= null;
			cd_categoria_w	:= null;
	end;
 
 
	SELECT * FROM Definir_Tipo_Ato_servico(cd_estabelecimento_w, cd_procedimento_p, cd_convenio_w, null, cd_cgc_prestador_w, ie_tipo_ato_sus_w, ie_tipo_servico_sus_w) INTO STRICT ie_tipo_ato_sus_w, ie_tipo_servico_sus_w;
 
	begin 
	select	cd_atividade_profissional, 
		ie_tipo_atend_bpa, 
		ie_grupo_atend_bpa 
	into STRICT	cd_atividade_profissional_w, 
		ie_tipo_atend_bpa_w, 
		ie_grupo_atend_bpa_w 
	from	sus_atividade_prof_proc 
	where	cd_procedimento	= cd_procedimento_p 
	and	ie_origem_proced	= ie_origem_proced_p;
	exception 
		when others then 
			begin 
			cd_atividade_profissional_w	:= null;
			ie_tipo_atend_bpa_w	:= null;
			ie_grupo_atend_bpa_w	:= null;
			end;
	end;
	 
	 
	select	Obter_Atepacu_paciente(nr_atendimento_p,'A') 
	into STRICT	nr_seq_interno_w 
	;
 
	select	dt_entrada_unidade 
	into STRICT	dt_entrada_unidade_w 
	from	atend_paciente_unidade 
	where	nr_seq_interno = nr_seq_interno_w;
	 
	select	nextval('procedimento_paciente_seq') 
	into STRICT	nr_sequencia_w 
	;
	 
 
	select	coalesce(max(cd_setor_exclusivo),0) 
	into STRICT	cd_setor_atendimento_w 
	from	procedimento 
	where	cd_procedimento		= cd_procedimento_p 
	and	ie_origem_proced	= ie_origem_proced_p;
	 
 
	if (cd_setor_atendimento_w = 0) then 
		select	coalesce(max(cd_setor_atendimento),0) 
		into STRICT	cd_setor_atendimento_w 
			from	procedimento_setor_atend 
		where	cd_procedimento		= cd_procedimento_p 
		and	ie_origem_proced	= ie_origem_proced_p 
		order by ie_prioridade desc;
	end if;
	 
	if (cd_setor_atendimento_w = 0) then 
		select 	Obter_setor_atendimento(nr_atendimento_p) 
		into STRICT	cd_setor_atendimento_w 
		;
	end if;
	 
	insert into procedimento_paciente(nr_sequencia, 
		nr_atendimento, 
		dt_entrada_unidade, 
		cd_procedimento,	 
		dt_procedimento, 
		qt_procedimento, 
		dt_atualizacao, 
		nm_usuario, 
		cd_convenio, 
		cd_categoria, 
		cd_acao, 
		cd_setor_atendimento, 
		ie_origem_proced, 
		tx_procedimento, 
		cd_cgc_prestador, 
		nm_usuario_original, 
		nr_seq_atepacu, 
		ie_auditoria, 
		cd_atividade_prof_bpa, 
		ie_tipo_servico_sus, 
		ie_tipo_ato_sus, 
		nr_nf_prestador)	 
	values (nr_sequencia_w, 
		nr_atendimento_p, 
		dt_entrada_unidade_w, 
		cd_procedimento_p, 
		clock_timestamp(), 
		qt_procedimento_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		cd_convenio_w, 
		cd_categoria_w, 
		1, 
		cd_setor_atendimento_w, 
		ie_origem_proced_p, 
		100, 
		cd_cgc_prestador_w, 
		nm_usuario_p, 
		nr_seq_interno_w, 
		'N', 
		cd_atividade_profissional_w, 
		ie_tipo_servico_sus_w, 
		ie_tipo_ato_sus_w, 
		substr(nr_nf_prestador_w,1,10));
	 
	ds_erro_w := consiste_exec_procedimento(nr_sequencia_w, ds_erro_w);
	CALL atualiza_preco_procedimento(nr_sequencia_w, cd_convenio_w, nm_usuario_p);
	end;
end if;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_proc_pac_sus_aih ( nr_atendimento_p bigint, NR_INTERNO_CONTA_P bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, qt_procedimento_p bigint, nr_nota_fiscal_p bigint, nm_usuario_p text) FROM PUBLIC;

