CREATE OR REPLACE PROCEDURE gerar_producao_lactario_js ( ie_consistir_p text, nr_seq_producao_p bigint, ie_excluir_itens_p text, cd_estabelecimento_p bigint, nm_usuario_p text, ds_pergunta_p INOUT text) AS $body$
DECLARE


ie_pergunta_usuario_w	varchar(1);


BEGIN

if (ie_consistir_p = 'S') then
	begin
	select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
	into STRICT	ie_pergunta_usuario_w
	from	nut_producao_lactario_item
	where	nr_seq_prod_lac = nr_seq_producao_p;

	if (ie_pergunta_usuario_w = 'S') then
		ds_pergunta_p	:= substr(obter_texto_tasy(68686, wheb_usuario_pck.get_nr_seq_idioma),1,255);
	end if;
	end;
end if;

if (ie_consistir_p = 'N') or (ie_pergunta_usuario_w = 'N') then
	begin
	CALL gerar_producao_lactario(
		nr_seq_producao_p,
		ie_excluir_itens_p,
		cd_estabelecimento_p,
		nm_usuario_p);
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
