SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_produto_adic_npt ( nr_seq_npt_p bigint, ds_lista_produto_p text, nm_usuario_p text) AS $body$
DECLARE



lista_informacao_w			varchar(800);
ie_contador_w			bigint	:= 0;
tam_lista_w			bigint;
ie_pos_virgula_w			smallint;
nr_seq_elem_mat_w		bigint;
nr_sequencia_w			bigint;
cd_unidade_medida_consumo_w	varchar(30);
cd_material_w			integer;
qt_volume_w			double precision;
nr_seq_elemento_w			bigint;
nr_seq_ele_w				bigint;
ie_prim_fase_w			varchar(1)	:= 'S';
ie_seg_fase_w			varchar(1)	:= 'S';
ie_terc_fase_w			varchar(1)	:= 'S';
ie_quar_fase_w			varchar(1)	:= 'S';
qt_fase_npt_w			bigint;
cd_unidade_medida_w		varchar(30);
cd_unidade_medida_ww	varchar(30);


c01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.cd_unidade_medida
from	nut_elem_material b,
	nut_elemento a
where	a.nr_sequencia	= b.nr_seq_elemento
and	a.ie_situacao	= 'A'
and	b.ie_situacao	= 'A'
and	a.ie_npt_adulto	= 'S'
and	b.cd_material	= cd_material_w
and	(a.cd_unidade_medida IS NOT NULL AND a.cd_unidade_medida::text <> '')
and	coalesce(b.ie_tipo,'NPT') = 'NPT'
and not exists (	select	1
			from	nut_pac_elemento c
			where	c.nr_seq_elemento 	= a.nr_sequencia
			and	nr_seq_nut_pac		= nr_seq_npt_p);


BEGIN

select	qt_fase_npt
into STRICT	qt_fase_npt_w
from	nut_pac
where	nr_sequencia	= nr_seq_npt_p;

lista_informacao_w	:= ds_lista_produto_p;

while	(lista_informacao_w IS NOT NULL AND lista_informacao_w::text <> '') or
	ie_contador_w > 200 loop
	begin
	tam_lista_w		:= length(lista_informacao_w);
	ie_pos_virgula_w	:= position(',' in lista_informacao_w);

	if (ie_pos_virgula_w <> 0) then
		nr_seq_elem_mat_w	:= (substr(lista_informacao_w,1,(ie_pos_virgula_w - 1)))::numeric;
		lista_informacao_w	:= substr(lista_informacao_w,(ie_pos_virgula_w + 1),tam_lista_w);
	end if;

	select	b.cd_unidade_medida_consumo,
			a.cd_material,
			a.nr_seq_elemento
	into STRICT	cd_unidade_medida_consumo_w,
			cd_material_w,
			nr_seq_ele_w
	from	material b,
			nut_elem_material a
	where	a.cd_material	= b.cd_material
	and		a.nr_sequencia	= nr_seq_elem_mat_w
	and	coalesce(a.ie_tipo,'NPT') = 'NPT';

	select	obter_conversao_ml(cd_material_w,1,cd_unidade_medida_consumo_w)
	into STRICT	qt_volume_w
	;

	update	NUT_PAC
	set	ie_editado = 'S'
	where	nr_sequencia = nr_seq_npt_p;

	select	nextval('nut_pac_elem_mat_seq')
	into STRICT	nr_sequencia_w
	;

	/*select	max(cd_unidade_medida)
	into	cd_unidade_medida_ww
	from	nut_elemento_unid_med a
	where	nr_seq_elemento	= nr_seq_ele_w
	and		ie_prioridade = (select min(ie_prioridade) from nut_elemento_unid_med b where b.nr_seq_elemento	= nr_seq_ele_w);*/
	select	max(cd_unidade_medida)
	into STRICT	cd_unidade_medida_ww
	from 	unidade_medida_dose_v a
	where	cd_material = cd_material_w
	and	ie_prioridade  not in (8,9)
	and	obter_se_unid_consumo(cd_material, obter_estabelecimento_ativo,ie_prioridade) = 'S'
	and	ie_prioridade = (SELECT min(ie_prioridade) from unidade_medida_dose_v b where a.cd_material = b.cd_material);


	insert into nut_pac_elem_mat(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		nr_seq_elem_mat,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_nut_pac,
		qt_volume,
		cd_material,
		cd_unidade_medida)
	values (	nr_sequencia_w,
		clock_timestamp(),
		nm_usuario_p,
		null,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_npt_p,
		qt_volume_w,
		cd_material_w,
		cd_unidade_medida_ww);

	open C01;
	loop
	fetch C01 into
		nr_seq_elemento_w,
		cd_unidade_medida_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (qt_fase_npt_w = 3) then
			ie_quar_fase_w	:= 'N';
		elsif (qt_fase_npt_w = 2) then
			ie_terc_fase_w	:= 'N';
			ie_quar_fase_w	:= 'N';
		elsif (qt_fase_npt_w = 1) then
			ie_terc_fase_w	:= 'N';
			ie_seg_fase_w	:= 'N';
			ie_quar_fase_w	:= 'N';
		end if;

		select	nextval('nut_pac_elemento_seq')
		into STRICT	nr_sequencia_w
		;

		insert into Nut_Pac_Elemento(
			nr_sequencia, nr_seq_nut_pac, nr_seq_elemento,
			dt_atualizacao, nm_usuario, cd_unidade_medida,
			qt_elem_kg_dia, qt_diaria, pr_total, qt_kcal,
			ie_prim_fase, ie_seg_fase, ie_terc_fase, ie_quar_fase, ie_npt)
		values (
			nr_sequencia_w, nr_seq_npt_p, nr_seq_elemento_w,
			clock_timestamp(), nm_usuario_p, cd_unidade_medida_w,
			0, 0, 0, 0, ie_prim_fase_w, ie_seg_fase_w, ie_terc_fase_w, ie_quar_fase_w, 'S');

		end;
	end Loop;
	close C01;

	ie_contador_w	:= ie_contador_w + 1;
	end;
end loop;

update	nut_pac
set	IE_EDITADO	= 'S'
where	nr_sequencia	= nr_seq_npt_p;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_produto_adic_npt ( nr_seq_npt_p bigint, ds_lista_produto_p text, nm_usuario_p text) FROM PUBLIC;

