CREATE OR REPLACE PROCEDURE gerar_prof_agenda_servico ( cd_agenda_p bigint, nr_seq_agenda_p bigint, nr_seq_turno_p bigint, dt_agenda_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
ie_profissional_w		varchar(1);
qt_registros_w			bigint;
dt_agenda_w			timestamp;

ie_gerar_prof_ordem_w		varchar(1);
nr_seq_ordem_max_w		bigint;
nr_seq_prof_w			bigint;


BEGIN 
 
select	substr(obter_se_agenda_prof(cd_agenda_p),1,1) 
into STRICT	ie_profissional_w
;
 
select	coalesce(max(ie_gerar_prof_hor),'N') 
into STRICT	ie_gerar_prof_ordem_w 
from	agenda 
where	cd_agenda	= cd_agenda_p;
 
select	count(*) 
into STRICT	qt_registros_w 
from	agenda_consulta_prof 
where	nr_seq_agenda	= nr_seq_agenda_p;
 
if (ie_profissional_w = 'S') and (qt_registros_w = 0) then 
 
	if (ie_gerar_prof_ordem_w = 'S') then 
		 
		select	max(a.nr_seq_ordem) 
		into STRICT	nr_seq_ordem_max_w 
		from	agenda_turno_prof a, 
			agenda_consulta_prof b, 
			agenda_consulta c 
		where	b.cd_pessoa_fisica	= a.cd_pessoa_fisica 
		and	b.nr_seq_ordem		= a.nr_seq_ordem 
		and	b.nr_seq_agenda		= c.nr_sequencia 
		and	a.nr_seq_turno		= nr_seq_turno_p 
		and	c.dt_agenda		= dt_agenda_p 
		and	c.cd_agenda		= cd_agenda_p;
		 
		select	min(nr_sequencia) 
		into STRICT	nr_seq_prof_w 
		from	agenda_turno_prof 
		where	nr_seq_turno	= nr_seq_turno_p 
		and	nr_seq_ordem 	> nr_seq_ordem_max_w;
				 
		insert into agenda_consulta_prof( 
			nr_sequencia, 
			nr_seq_agenda, 
			dt_atualizacao, 
			nm_usuario, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			nr_seq_ordem, 
			ie_tipo_profissional, 
			cd_pessoa_fisica, 
			ie_solicita_retorno 
		) 
		SELECT	nextval('agenda_consulta_prof_seq'), 
			nr_seq_agenda_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			nr_seq_ordem, 
			ie_tipo_profissional, 
			cd_pessoa_fisica, 
			ie_solicita_retorno 
		from	agenda_turno_prof 
		where	nr_sequencia	= nr_seq_prof_w;
	 
	else 
		insert into agenda_consulta_prof( 
			nr_sequencia, 
			nr_seq_agenda, 
			dt_atualizacao, 
			nm_usuario, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			nr_seq_ordem, 
			ie_tipo_profissional, 
			cd_pessoa_fisica, 
			ie_solicita_retorno 
		) 
		SELECT	nextval('agenda_consulta_prof_seq'), 
			nr_seq_agenda_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			nr_seq_ordem, 
			ie_tipo_profissional, 
			cd_pessoa_fisica, 
			ie_solicita_retorno 
		from	agenda_turno_prof 
		where	nr_seq_turno	= nr_seq_turno_p;
	 
	end if;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
