SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_prog_entrega_item_cot ( nr_cot_compra_p text, cd_material_p text, qt_item_p text, dt_entrega_p text, cd_cgc_fornecedor_p text, ie_deleta_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_cot_forn_item_w		bigint;
nr_seq_cot_forn_w			bigint;
nr_item_cot_compra_w		integer;
nr_sequencia_w			bigint;
dt_entrega_w			timestamp;
dt_entrega_ww			timestamp;
qt_conv_compra_estoque_w		double precision;
qt_item_w			double precision;
cd_estabelecimento_w		bigint;
ie_forma_calc_entrega_w		varchar(15);
dt_entrega_param_w		varchar(20);
cd_material_w			bigint;
cd_material_ww			bigint;
qt_entrega_w			double precision;
qt_numero_entregas_w		bigint;
qt_material_w			double precision;

c01 CURSOR FOR 
SELECT	dt_entrega, 
	qt_entrega 
from	cot_compra_item_entrega 
where	nr_cot_compra		= nr_cot_compra_p 
and	nr_item_cot_compra	= nr_item_cot_compra_w;


BEGIN 
 
cd_material_w		:= cd_material_p;
dt_entrega_param_w	:= dt_entrega_p;
qt_item_w		:= qt_item_p;
 
select	cd_estabelecimento 
into STRICT	cd_estabelecimento_w 
from	cot_compra 
where	nr_cot_compra	= nr_cot_compra_p;
 
select	substr(coalesce(obter_valor_param_usuario(915, 67, Obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w),'L'),1,1) 
into STRICT	ie_forma_calc_entrega_w
;
 
select	coalesce(min(b.nr_item_cot_compra),0) 
into STRICT	nr_item_cot_compra_w 
from	cot_compra_forn a, 
	cot_compra_forn_item b 
where	a.nr_sequencia		= b.nr_seq_cot_forn 
and	b.cd_material		= cd_material_w 
and	b.nr_cot_compra		= nr_cot_compra_p 
and	a.cd_cgc_fornecedor	= cd_cgc_fornecedor_p;
 
if (nr_item_cot_compra_w = 0) then 
 
	select	coalesce(min(a.cd_material),0) 
	into STRICT	cd_material_ww 
	from	material a 
	where	a.cd_material_generico = cd_material_w 
	and	ie_situacao = 'A' 
	and	exists ( 
		SELECT	1 
		from	cot_compra_forn x, 
			cot_compra_forn_item y 
		where	x.nr_sequencia		= y.nr_seq_cot_forn 
		and	x.nr_cot_compra		= nr_cot_compra_p 
		and	x.cd_cgc_fornecedor	= cd_cgc_fornecedor_p 
		and	y.cd_material		= a.cd_material);
	 
	if (cd_material_ww = 0) then 
	 
		select	coalesce(min(a.cd_material),0) 
		into STRICT	cd_material_ww 
		from	material a 
		where	a.cd_material_estoque = cd_material_w 
		and	ie_situacao = 'A' 
		and	exists ( 
			SELECT	1 
			from	cot_compra_forn x, 
				cot_compra_forn_item y 
			where	x.nr_sequencia		= y.nr_seq_cot_forn 
			and	x.nr_cot_compra		= nr_cot_compra_p 
			and	x.cd_cgc_fornecedor	= cd_cgc_fornecedor_p 
			and	y.cd_material		= a.cd_material);
	end if;
	 
	if (cd_material_ww > 0) then 
	 
		select	coalesce(min(b.nr_item_cot_compra),0) 
		into STRICT	nr_item_cot_compra_w 
		from	cot_compra_forn a, 
			cot_compra_forn_item b 
		where	a.nr_sequencia		= b.nr_seq_cot_forn 
		and	b.cd_material		= cd_material_ww 
		and	b.nr_cot_compra		= nr_cot_compra_p 
		and	a.cd_cgc_fornecedor	= cd_cgc_fornecedor_p;
	end if;	
end if;
	 
if (nr_item_cot_compra_w > 0) then 
 
	begin	 
	select	coalesce(max(qt_conv_compra_estoque),0) 
	into STRICT	qt_conv_compra_estoque_w 
	from	material 
	where	cd_material = cd_material_w;
	 
	select	max(nr_sequencia), 
		max(nr_seq_cot_forn) 
	into STRICT	nr_seq_cot_forn_item_w, 
		nr_seq_cot_forn_w 
	from	cot_compra_forn_item 
	where	nr_cot_compra		= nr_cot_compra_p 
	and	nr_item_cot_compra	= nr_item_cot_compra_w 
	and	cd_cgc_fornecedor		= cd_cgc_fornecedor_p;
 
	if (ie_deleta_p = 'S') then 
		delete	from cot_compra_forn_item_en 
		where	nr_seq_cot_item_forn = nr_seq_cot_forn_item_w;
	end if;	
	 
	/*XML com programação de entrega*/
 
	if (dt_entrega_p IS NOT NULL AND dt_entrega_p::text <> '') then 
	 
		select	max(dt_entrega) 
		into STRICT	dt_entrega_w 
		from	cot_compra_item_entrega 
		where	nr_cot_compra		= nr_cot_compra_p 
		and	nr_item_cot_compra	= nr_item_cot_compra_w;
		 
		if (obter_dados_parametro_compras(cd_estabelecimento_w, 24) = 'S') and (qt_conv_compra_estoque_w > 0) then 
			qt_item_w := dividir(qt_item_p, qt_conv_compra_estoque_w);
		end if;
 
		dt_entrega_param_w := to_char(to_date(dt_entrega_p,'dd/mm/yy'),'dd/mm/yyyy');
	 
		if (ie_forma_calc_entrega_w = 'L') then 
			dt_entrega_ww	:= coalesce(to_date(dt_entrega_param_w,'dd/mm/yyyy'),dt_entrega_w) + coalesce(obter_dados_cot_compra_forn(nr_cot_compra_p, nr_seq_cot_forn_w, 'DE'), 0);
		elsif (ie_forma_calc_entrega_w = 'F') then		 
			dt_entrega_ww	:= coalesce(to_date(dt_entrega_param_w,'dd/mm/yyyy'),dt_entrega_w);
		else	dt_entrega_ww	:= trunc(clock_timestamp(),'dd') + coalesce(obter_dados_cot_compra_forn(nr_cot_compra_p, nr_seq_cot_forn_w, 'DE'), 0);
		end if;	
	 
		select	nextval('cot_compra_forn_item_en_seq') 
		into STRICT	nr_sequencia_w 
		;
 
		insert into cot_compra_forn_item_en( 
			nr_sequencia, 
			dt_atualizacao, 
			nm_usuario, 
			nr_seq_cot_item_forn, 
			dt_entrega, 
			qt_entrega, 
			nr_cot_compra, 
			nr_item_cot_compra) 
		values (	nr_sequencia_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			nr_seq_cot_forn_item_w, 
			dt_entrega_ww, 
			qt_item_w, 
			nr_cot_compra_p, 
			nr_item_cot_compra_w);
	else 
		/*XML com programação de entrega*/
 
		open C01;
		loop 
		fetch C01 into	 
			dt_entrega_w, 
			qt_entrega_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin 
			 
			if (obter_dados_parametro_compras(cd_estabelecimento_w, 24) = 'S') and (qt_conv_compra_estoque_w > 0) then 
				qt_entrega_w := dividir(qt_entrega_w, qt_conv_compra_estoque_w);
			end if;
 
			dt_entrega_param_w := to_char(to_date(dt_entrega_p,'dd/mm/yy'),'dd/mm/yyyy');
			 
			if (ie_forma_calc_entrega_w = 'L') then 
				dt_entrega_ww	:= coalesce(to_date(dt_entrega_param_w,'dd/mm/yyyy'),dt_entrega_w) + coalesce(obter_dados_cot_compra_forn(nr_cot_compra_p, nr_seq_cot_forn_w, 'DE'), 0);
			elsif (ie_forma_calc_entrega_w = 'F') then		 
				dt_entrega_ww	:= coalesce(to_date(dt_entrega_param_w,'dd/mm/yyyy'),dt_entrega_w);
			else	dt_entrega_ww	:= trunc(clock_timestamp(),'dd') + coalesce(obter_dados_cot_compra_forn(nr_cot_compra_p, nr_seq_cot_forn_w, 'DE'), 0);		
			end if;	
			 
			select	nextval('cot_compra_forn_item_en_seq') 
			into STRICT	nr_sequencia_w 
			;
 
			insert into cot_compra_forn_item_en( 
				nr_sequencia, 
				dt_atualizacao, 
				nm_usuario, 
				nr_seq_cot_item_forn, 
				dt_entrega, 
				qt_entrega, 
				nr_cot_compra, 
				nr_item_cot_compra) 
			values (	nr_sequencia_w, 
				clock_timestamp(), 
				nm_usuario_p, 
				nr_seq_cot_forn_item_w, 
				dt_entrega_ww, 
				qt_entrega_w, 
				nr_cot_compra_p, 
				nr_item_cot_compra_w);
			end;
		end loop;
		close C01;
	end if;	
	 
	if (coalesce(dt_entrega_p::text, '') = '') then 
	 
		select	count(*) 
		into STRICT	qt_numero_entregas_w 
		from	cot_compra_forn_item_en 
		where	nr_seq_cot_item_forn 	= nr_seq_cot_forn_item_w;
	 
		if (qt_numero_entregas_w = 1) then 
		 
			select	coalesce(sum(qt_entrega),0) 
			into STRICT	qt_entrega_w 
			from	cot_compra_forn_item_en 
			where	nr_seq_cot_item_forn 	= nr_seq_cot_forn_item_w;
		 
			select	coalesce(max(qt_material),0) 
			into STRICT	qt_material_w 
			from	cot_compra_forn_item 
			where	nr_sequencia = nr_seq_cot_forn_item_w;
		 
		if (qt_entrega_w > 0) and (qt_entrega_w <> qt_material_w) then 
			 
			update	cot_compra_forn_item_en 
			set	qt_entrega 		= qt_material_w 
			where	nr_seq_cot_item_forn 	= nr_seq_cot_forn_item_w;
		end if;	
	end if;
	 
	end if;	
	end;
end if;	
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_prog_entrega_item_cot ( nr_cot_compra_p text, cd_material_p text, qt_item_p text, dt_entrega_p text, cd_cgc_fornecedor_p text, ie_deleta_p text, nm_usuario_p text) FROM PUBLIC;

