CREATE OR REPLACE PROCEDURE gerar_proj_desenv_migr_funcao ( nr_seq_gerencia_p bigint, cd_gerente_p text, cd_funcao_p bigint, nr_seq_projeto_p INOUT bigint, nm_usuario_p text) AS $body$
DECLARE

		 
ie_projeto_migracao_w	varchar(1);
nr_seq_projeto_w	bigint := null;
nr_seq_cronograma_w	bigint := null;
nr_seq_estagio_w	bigint := null;
		

BEGIN 
if (nr_seq_gerencia_p IS NOT NULL AND nr_seq_gerencia_p::text <> '') and (cd_gerente_p IS NOT NULL AND cd_gerente_p::text <> '') and (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	select	coalesce(max(nr_sequencia),0) 
	into STRICT	nr_seq_projeto_w 
	from	proj_projeto 
	
	where	nr_seq_gerencia = nr_seq_gerencia_p 
	and	nr_seq_classif = 14 
	and	cd_funcao = cd_funcao_p;	
 
	if (nr_seq_projeto_w = 0) then 
		begin 
		select	nextval('proj_projeto_seq') 
		into STRICT	nr_seq_projeto_w 
		;
		 
		insert into proj_projeto( 
			nr_sequencia, 
			dt_projeto, 
			nr_seq_gerencia, 
			nr_seq_grupo_des, 
			nr_seq_classif, 
			ds_titulo, 
			cd_coordenador, 
			nr_seq_cliente, 
			cd_gerente_cliente, 
			nr_seq_estagio, 
			ie_status, 
			cd_funcao, 
			ie_origem, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			dt_atualizacao, 
			nm_usuario, 
			ie_ref_migr) 
		values ( 
			nr_seq_projeto_w, 
			clock_timestamp(), 
			--9, 
			nr_seq_gerencia_p, 
			--decode(nr_seq_gerencia_p, 3, 19, 4, 50, 7, 63), 
			obter_grupo_desenv_resp_funcao(cd_funcao_p), 
			14, 
			substr(obter_desc_funcao(cd_funcao_p),1,80), 
			--'4464', 
			obter_pessoa_fisica_usuario(nm_usuario_p, 'C'), 
			1800, 
			cd_gerente_p, 
			1, 
			'P', 
			cd_funcao_p, 
			'D', 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			1);
		end;
	else 
		begin 
		select	max(nr_seq_estagio) 
		into STRICT	nr_seq_estagio_w 
		from	proj_projeto 
		where	nr_sequencia = nr_seq_projeto_w;
		 
		if (nr_seq_estagio_w = 1) then 
			begin 
			nr_seq_cronograma_w := proj_desenv_obter_cron_ativ_os(nr_seq_projeto_w);
				 
			if (nr_seq_cronograma_w > 0) then 
				begin 
				CALL regerar_cron_proj_migr_funcao(nr_seq_cronograma_w, cd_funcao_p, nm_usuario_p);
				end;
			end if;
			end;
		end if;
		nr_seq_projeto_w := 0;
		end;
	end if;
	end;
end if;
nr_seq_projeto_p := nr_seq_projeto_w;
commit;
end;
$body$
