CREATE OR REPLACE PROCEDURE gerar_proj_des_sem_anal_migr ( ie_gerencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

	 
ie_gerencia_w		smallint;
ds_funcao_w		varchar(80);
cd_funcao_w		bigint;
ds_form_w		varchar(15);
nr_seq_projeto_w	bigint;
nr_seq_gerencia_w	bigint;
cd_gerente_w		varchar(10);
	
c01 CURSOR FOR 
SELECT	a.ie_gerencia, 
	a.ds_funcao, 
	a.cd_funcao, 
	a.ds_form	 
from	funcao_migracao_v a 
where	a.ie_gerencia = ie_gerencia_p 
and	not exists ( 
		SELECT	1 
		from	proj_projeto x 
		where	x.cd_funcao = a.cd_funcao) 
group by 
	a.ie_gerencia, 
	a.ds_funcao, 
	a.cd_funcao, 
	a.ds_form;
	

BEGIN 
if (ie_gerencia_p IS NOT NULL AND ie_gerencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	open c01;
	loop 
	fetch c01 into	ie_gerencia_w, 
			ds_funcao_w, 
			cd_funcao_w, 
			ds_form_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin 
		if (ie_gerencia_w = 1) then 
			begin 
			nr_seq_gerencia_w	:= 7;
			cd_gerente_w		:= '4995';
			end;
		elsif (ie_gerencia_w = 2) then 
			begin 
			nr_seq_gerencia_w	:= 4;
			cd_gerente_w		:= '442';
			end;		
		elsif (ie_gerencia_w = 3) then 
			begin 
			nr_seq_gerencia_w	:= 3;
			cd_gerente_w		:= '130';
			end;					
		end if;
		 
		nr_seq_projeto_w := gerar_proj_desenv_migr_funcao( 
			nr_seq_gerencia_w, cd_gerente_w, cd_funcao_w, nr_seq_projeto_w, nm_usuario_p);
			 
		if (nr_seq_projeto_w > 0) then 
			begin 
			CALL gerar_equipes_proj_migr_funcao( 
				nr_seq_projeto_w, 
				cd_gerente_w, 
				nm_usuario_p);
				 
			CALL gerar_cron_proj_migr_funcao( 
				nr_seq_projeto_w, 
				cd_gerente_w, 
				cd_funcao_w, 
				nm_usuario_p);				
			end;
		end if;
		end;
	end loop;
	close c01;
	end;
end if;
commit;
end;
$body$
