SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_prontuario_pac_manual ( nr_prontuario_p bigint, cd_pessoa_fisica_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text, nm_usuario_p text) AS $body$
DECLARE


ie_regra_pront_w		varchar(15);
nr_prontuario_w		bigint;
ds_erro_w		varchar(255) := 'X';
qt_existe_w		bigint;
nr_sequencia_w		bigint;


BEGIN

select	coalesce(max(vl_parametro),'BASE')
into STRICT	ie_regra_pront_w
from	funcao_parametro
where	cd_funcao = 0
and	nr_sequencia = 120;

if (nr_prontuario_p IS NOT NULL AND nr_prontuario_p::text <> '') then

	if (ie_regra_pront_w = 'BASE') then

		select	max(nr_prontuario)
		into STRICT	nr_prontuario_w
		from	pessoa_fisica
		where	(nr_prontuario IS NOT NULL AND nr_prontuario::text <> '');

		if (nr_prontuario_p > nr_prontuario_w) then
			ds_erro_w := substr(WHEB_MENSAGEM_PCK.get_texto(280863),1,255);
		else
			select	count(*)
			into STRICT	qt_existe_w
			from	pessoa_fisica
			where	nr_prontuario = nr_prontuario_p;

			if (qt_existe_w > 0) then
				ds_erro_w := substr(WHEB_MENSAGEM_PCK.get_texto(280865),1,255);
			end if;
		end if;

		if (ds_erro_w = 'X') then
			update	pessoa_fisica
			set	nr_prontuario = nr_prontuario_p
			where	cd_pessoa_fisica = cd_pessoa_fisica_p;
		end if;

	elsif (ie_regra_pront_w = 'ESTAB') and (coalesce(cd_estabelecimento_p,0) <> 0) then

		select	max(nr_prontuario)
		into STRICT	nr_prontuario_w
		from	pessoa_fisica_pront_estab
		where	cd_estabelecimento = cd_estabelecimento_p;

		if (nr_prontuario_p > nr_prontuario_w) then
			ds_erro_w := substr(WHEB_MENSAGEM_PCK.get_texto(280863),1,255);
		else

			select	count(*)
			into STRICT	qt_existe_w
			from	pessoa_fisica_pront_estab
			where	cd_estabelecimento = cd_estabelecimento_p
			and	nr_prontuario = nr_prontuario_p;

			if (qt_existe_w > 0) then
				ds_erro_w := substr(WHEB_MENSAGEM_PCK.get_texto(280865),1,255);
			end if;

			if (ds_erro_w = 'X') then

				select	coalesce(max(nr_sequencia),0)
				into STRICT	nr_sequencia_w
				from	pessoa_fisica_pront_estab
				where	cd_pessoa_fisica = cd_pessoa_fisica_p
				and	cd_estabelecimento = cd_estabelecimento_p;

				if (nr_sequencia_w = 0) then

					insert into pessoa_fisica_pront_estab(
						nr_sequencia,
						cd_pessoa_fisica,
						cd_estabelecimento,
						nr_prontuario,
						nm_usuario,
						dt_atualizacao,
						nm_usuario_nrec,
						dt_atualizacao_nrec)
					values (	nextval('pessoa_fisica_pront_estab_seq'),
						cd_pessoa_fisica_p,
						cd_estabelecimento_p,
						nr_prontuario_p,
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp());
				else

					update	pessoa_fisica_pront_estab
					set	nr_prontuario = nr_prontuario_p
					where	nr_sequencia = nr_sequencia_w;

				end if;
			end if;
		end if;
	end if;

end if;

ds_erro_p := ds_erro_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_prontuario_pac_manual ( nr_prontuario_p bigint, cd_pessoa_fisica_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text, nm_usuario_p text) FROM PUBLIC;

