CREATE OR REPLACE PROCEDURE gerar_propaci_equip_hc_js ( nr_seq_pac_equip_p bigint, nr_seq_equipamento_p bigint, nr_atendimento_origem_p bigint, nr_seq_equip_hosp_p bigint, nm_usuario_p text, ds_msg_p INOUT text) AS $body$
DECLARE

ie_equip_proced_p	varchar(1);
ie_tipo_equip_w		varchar(1);

BEGIN
ie_tipo_equip_w := obter_param_usuario(867, 84, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_tipo_equip_w);
if (ie_tipo_equip_w = 'E') then 
 
 
	if (coalesce(nr_atendimento_origem_p::text, '') = '') then 
		ds_msg_p := substr(obter_texto_tasy(59254, wheb_usuario_pck.get_nr_seq_idioma),1,255);
	else 
		CALL gerar_propaci_equip_hc(nr_atendimento_origem_p, nr_seq_equip_hosp_p, nr_seq_pac_equip_p, nm_usuario_p);
	end if;
     
else 
 
	Select CASE WHEN count(*)=1 THEN  'S'  ELSE 'N' END  
	into STRICT  ie_equip_proced_p 
	from  hc_equipamento 
	where nr_sequencia = nr_seq_equipamento_p 
	and  (cd_procedimento IS NOT NULL AND cd_procedimento::text <> '');
	if (ie_equip_proced_p = 'S') then 
		begin 
		if (coalesce(nr_atendimento_origem_p::text, '') = '') then 
			ds_msg_p := substr(obter_texto_tasy(59254, wheb_usuario_pck.get_nr_seq_idioma),1,255);
		else 
			CALL gerar_propaci_equip_hc(nr_atendimento_origem_p, nr_seq_equipamento_p, nr_seq_pac_equip_p, nm_usuario_p);
		end if;
		end;
	end if;
end if;
end;
$body$
LANGUAGE PLPGSQL
