SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_propaci_regra_qtde_uso (nr_interno_conta_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_acao_excesso_p text, qt_ajuste_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ie_classificacao_w	varchar(1);
nr_sequencia_w		bigint;
nr_seq_novo_w		bigint;
cd_convenio_w		integer;


BEGIN 
 
select	cd_convenio_parametro 
into STRICT	cd_convenio_w 
from	conta_paciente 
where	nr_interno_conta	= nr_interno_conta_p;
 
select	ie_classificacao 
into STRICT	ie_classificacao_w 
from	procedimento 
where	cd_procedimento		= cd_procedimento_p 
and	ie_origem_proced	= ie_origem_proced_p;
 
/* A princípio só ajustar para cima OS 12700*/
 
if (coalesce(ie_acao_excesso_p::text, '') = '') and (qt_ajuste_p <> 0) then 
 
	select	max(nr_sequencia) 
	into STRICT	nr_sequencia_w 
	from	procedimento_paciente 
	where	nr_interno_conta	= nr_interno_conta_p 
	and	cd_procedimento		= cd_procedimento_p 
	and	ie_origem_proced	= ie_origem_proced_p;
 
	nr_seq_novo_w := duplicar_proc_paciente(nr_sequencia_w, nm_usuario_p, nr_seq_novo_w);
 
	update	procedimento_paciente 
	set	qt_procedimento	= qt_ajuste_p, 
		dt_atualizacao	= clock_timestamp(), 
		nm_usuario	= nm_usuario_p 
	where	nr_sequencia	= nr_seq_novo_w;
 
	if (ie_classificacao_w = 1) or (ie_classificacao_w = 3) then 
		CALL atualiza_preco_procedimento(nr_seq_novo_w, cd_convenio_w, nm_usuario_p);
	elsif (ie_classificacao_w = 2) then 
		CALL atualiza_preco_servico(nr_seq_novo_w, nm_usuario_p);
	end if;
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
