CREATE OR REPLACE PROCEDURE gerar_protocolos_presc_sae (nr_prescr_sae_p bigint, nm_usuario_p text, nr_prescricao_p bigint, ds_protocolos_p text default ' ') AS $body$
DECLARE

									 
ds_protocolos_w				varchar(1000);
nr_seq_protocolo_w			bigint;
cd_protocolo_w				bigint;
cd_intervalo_w				varchar(7);
HR_PRIM_HORARIO_w			varchar(10);
nr_seq_pe_proc_w			bigint;
nr_sequencia_protocolo_w		bigint;
cd_setor_atendimento_w		integer;

C02 CURSOR FOR 
	SELECT	a.nr_seq_protocolo, 
		b.cd_intervalo, 
		b.HR_PRIM_HORARIO, 
		b.nr_sequencia 
	from	pe_prescr_proc b, 
		pe_interv_protocolo a 
	where	b.nr_seq_proc	= a.nr_seq_interv 
	and	b.nr_seq_prescr	= nr_prescr_sae_p 
	and ((ds_protocolos_w = ' ') or (obter_se_contido(a.nr_seq_protocolo,ds_protocolos_w) = 'S')) 
	and obter_se_setor_pe_interv(cd_setor_atendimento_w,a.nr_sequencia) = 'S';
									

BEGIN 
 
select	coalesce((SUBSTR(obter_unidade_atendimento(nr_atendimento,'IA','CS'),1,5))::numeric ,(SUBSTR(obter_unidade_atendimento(nr_atendimento,'A','CS'),1,5))::numeric ) cd_setor_atendimento 
	into STRICT	cd_setor_atendimento_w 
	from	pe_prescricao 
	where	nr_sequencia = nr_prescr_sae_p 
	and	coalesce(ie_situacao,'A') = 'A';
	 
ds_protocolos_w := coalesce(ds_protocolos_p,' ');
	if (substr(ds_protocolos_p,length(ds_protocolos_p)) = ',') then 
		ds_protocolos_w := substr(ds_protocolos_p,1,length(ds_protocolos_p)-1);
	end if;
	 
	update	pe_prescricao 
	set		ds_protocolo = ds_protocolos_w 
	where	nr_sequencia = nr_prescr_sae_p;
	 
	commit;
 
	open C02;
	loop 
	fetch C02 into 
		nr_seq_protocolo_w, 
		cd_intervalo_w, 
		HR_PRIM_HORARIO_w, 
		nr_seq_pe_proc_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
		 
		select	max(cd_protocolo), 
			max(nr_sequencia) 
		into STRICT	cd_protocolo_w, 
			nr_sequencia_protocolo_w 
		from	protocolo_medicacao 
		where	nr_Seq_Interna	= nr_seq_protocolo_w;
							 
		CALL INSERIR_ITEM_PROTOCOLO_PRESCR(nr_prescricao_p,cd_protocolo_w,nr_sequencia_protocolo_w,cd_intervalo_w,1,nm_usuario_p,null,clock_timestamp(),HR_PRIM_HORARIO_w,null,0);
		 
		update	prescr_material 
		set	nr_ocorrencia	= obter_ocorrencia_intervalo(cd_intervalo,24,'O'), 
			qt_total_dispensar = CASE WHEN obter_ocorrencia_intervalo(cd_intervalo,24,'O')=0 THEN 1  ELSE obter_ocorrencia_intervalo(cd_intervalo,24,'O') END  * qt_dose 
		where	nr_prescricao	= nr_prescricao_p;
		 
		update	prescr_material 
		set	nr_seq_pe_proc	= nr_seq_pe_proc_w 
		where	nr_prescricao	= nr_prescricao_p 
		and	coalesce(nr_seq_pe_proc::text, '') = '';
		 
		 
		end;
	end loop;
	close C02;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
