SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_protocolo_cirurgia ( nr_cirurgia_p bigint, nr_seq_protocolo_p bigint, nm_usuario_p text, nr_seq_pepo_p bigint, nr_seq_gerada_p INOUT bigint, ie_macro_p text default 'N') AS $body$
DECLARE



ds_cirurgia_w		varchar(32764);
nr_sequencia_w		bigint;
cd_pessoa_fisica_w	varchar(10);
qt_proc_realizados_w	bigint;
nr_seq_procedimento_w		bigint;
nr_atendimento_w	bigint;



BEGIN
if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then -- gerava NO DATA FOUND
	begin
	/*
	select	ds_cirurgia
	into	ds_cirurgia_w
	from	cirurgia_protocolo
	where	nr_sequencia = nr_seq_protocolo_p;
*/
	select 	count(*)
	into STRICT	qt_proc_realizados_w
	from 	procedimento_paciente a,
			cirurgia b,
			procedimento c
	where   a.nr_cirurgia = b.nr_cirurgia
	and	   a.cd_procedimento 	= c.cd_procedimento
	and	   a.ie_origem_proced 	= c.ie_origem_proced
	and	   c.ie_classificacao 	= 1
	and	   b.ie_status_cirurgia not in (3,4)
	and	   b.nr_seq_pepo = nr_seq_pepo_p;

	if (qt_proc_realizados_w = 1) then
		select 	nr_sequencia
		into STRICT	nr_seq_procedimento_w
		from 	procedimento_paciente a,
				cirurgia b,
				procedimento c
		where   a.nr_cirurgia = b.nr_cirurgia
		and	   a.cd_procedimento 	= c.cd_procedimento
		and	   a.ie_origem_proced 	= c.ie_origem_proced
		and	   c.ie_classificacao 	= 1
		and	   b.ie_status_cirurgia not in (3,4)
		and	   b.nr_seq_pepo = nr_seq_pepo_p;
	else
		nr_seq_procedimento_w := null;
	end if;


	select	nextval('cirurgia_descricao_seq')
	into STRICT	nr_sequencia_w
	;

	select	cd_pessoa_fisica
	into STRICT	cd_pessoa_fisica_w
	from	usuario
	where	nm_usuario	= nm_usuario_p;

	Insert into Cirurgia_Descricao(
		nr_sequencia,
		nr_cirurgia,
		dt_atualizacao,
		nm_usuario,
		ds_diagnostico,
		ds_cirurgia,
		ds_resumo_cirurgia,
		ds_diagnostico_pos,
		ds_exame_rad,
		ds_exame_anatomo,
		ds_achados_operatorios,
		cd_responsavel,
		ie_tipo_descricao,
		nr_seq_protocolo,
		nr_seq_pepo,
		dt_registro,
		ie_situacao,
		nr_seq_procedimento,
		cd_doenca_cid,
		ds_intercorrencia,
		ds_po_imediato,
      cd_doenca_cid_pre)
	SELECT	nr_sequencia_w,
		nr_cirurgia_p,
		clock_timestamp(),
		nm_usuario_p,
		ds_diagnostico,
		' ',
		ds_resumo_cirurgia,
		ds_diagnostico_pos,
		ds_exame_rad,
		ds_exame_anatomo,
		ds_achados_operatorios,
		cd_pessoa_fisica_w,
		'P',
		nr_seq_protocolo_p,
		nr_seq_pepo_p,
		clock_timestamp(),
		'A',
		nr_seq_procedimento_w,
		cd_doenca_cid,
		ds_intercorrencia,
		ds_po_imediato,
      cd_doenca_cid_pre
	from	cirurgia_protocolo
	where	nr_sequencia = nr_seq_protocolo_p;
/*
	update	Cirurgia_Descricao
	set	ds_cirurgia	= ds_cirurgia_w
	where	nr_sequencia	= nr_sequencia_w;*/
	CALL copia_campo_long_de_para(	'CIRURGIA_PROTOCOLO',
				'DS_CIRURGIA',
				' where nr_sequencia = :nr_sequencia ',
				'nr_sequencia='||nr_seq_protocolo_p,
				'CIRURGIA_DESCRICAO',
				'DS_CIRURGIA',
				' where nr_sequencia = :nr_sequencia',
				'nr_sequencia='||nr_sequencia_w);


	end;

	nr_seq_gerada_p := nr_sequencia_w;

	if (ie_macro_p	= 'S') then

		select	ds_cirurgia
		into STRICT	ds_cirurgia_w
		from	CIRURGIA_DESCRICAO
		where	nr_sequencia	= nr_sequencia_w;

		select	max(nr_atendimento)
		into STRICT	nr_atendimento_w
		from	cirurgia
		where	nr_cirurgia	= nr_cirurgia_p;

		CALL substituir_macro_protocolo_pa(nr_seq_gerada_p,ds_cirurgia_w,nr_atendimento_w,nm_usuario_p,'DC',nr_cirurgia_p);
	end if;

end if;
COMMIT;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_protocolo_cirurgia ( nr_cirurgia_p bigint, nr_seq_protocolo_p bigint, nm_usuario_p text, nr_seq_pepo_p bigint, nr_seq_gerada_p INOUT bigint, ie_macro_p text default 'N') FROM PUBLIC;

