CREATE OR REPLACE PROCEDURE gerar_prot_caso_teste_funcao ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w			bigint;
nr_seq_item_w			bigint;
nr_seq_item_aux_w			bigint;
cd_funcao_w			integer;
ds_item_w			varchar(80);
ds_acao_w			varchar(2000);
ds_result_w			varchar(2000);

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	teste_funcao
	where	cd_funcao = cd_funcao_w
	and	ie_situacao = 'A';

c02 CURSOR FOR
	SELECT	nr_sequencia,
		ds_item
	from	teste_funcao_item
	where	nr_seq_teste = nr_sequencia_w
	and	ie_situacao = 'A';

c03 CURSOR FOR
	SELECT	a.ds_acao,
		b.ds_resultado
	from	teste_func_item_result b,
		teste_funcao_item_acao a
	where	b.nr_seq_acao = a.nr_sequencia
	and	a.nr_seq_item = nr_seq_item_aux_w
	and	a.ie_situacao = 'A'
	and	b.ie_situacao = 'A';


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	select	cd_funcao
	into STRICT	cd_funcao_w
	from	teste_caso_teste
	where	nr_sequencia = nr_sequencia_p;

	open c01;
	loop
	fetch c01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		open c02;
		loop
		fetch c02 into
			nr_seq_item_aux_w,
			ds_item_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin
			select	nextval('teste_caso_teste_item_seq')
			into STRICT	nr_seq_item_w
			;

			insert into teste_caso_teste_item(
					nr_sequencia,
					nr_seq_caso_teste,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_apres,
					ds_item)
				values (	nr_seq_item_w,
					nr_sequencia_p,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					null,
					ds_item_w);
			open c03;
			loop
			fetch c03 into
				ds_acao_w,
				ds_result_w;
			EXIT WHEN NOT FOUND; /* apply on c03 */
				begin
				insert into teste_caso_teste_it_acao(
					nr_sequencia,
					nr_seq_item,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_apres,
					ds_premissas,
					ds_acao,
					ds_result_esperado,
					ds_observacao,
					cd_perfil)
				values (	nextval('teste_caso_teste_it_acao_seq'),
					nr_seq_item_w,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					null,
					null,
					ds_acao_w,
					ds_result_w,
					null,
					null);
				end;
			end loop;
			close c03;
			end;
		end loop;
		close c02;
		end;
	end loop;
	close c01;
	commit;
	end;
end if;

end;
$body$
