CREATE OR REPLACE PROCEDURE gerar_qt_disp_comp_solic_kit (nr_sequencia_p bigint) AS $body$
DECLARE


cd_material_w		integer;
nr_seq_item_w		integer;
qt_disponivel_w		double precision;
cd_local_estoque_w	smallint;
cd_estabelecimento_w	smallint;
qt_disp_estoque_w	double precision;
cd_unidade_medida_w	varchar(30);

C01 CURSOR FOR
SELECT	a.cd_material,
	a.nr_seq_item_kit
from	solic_kit_mat_comp a
where	nr_seq_solic_kit = nr_sequencia_p;


BEGIN

select	cd_estabelecimento,
	cd_local_estoque
into STRICT	cd_estabelecimento_w,
	cd_local_estoque_w
from	solic_kit_material
where	nr_sequencia = nr_sequencia_p;

open C01;
loop
fetch C01 into
	cd_material_w,
	nr_seq_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	cd_unidade_medida_estoque
	into STRICT	cd_unidade_medida_w
	from	material
	where	cd_material = cd_material_w;

	qt_disp_estoque_w := obter_saldo_disp_estoque( 	cd_estabelecimento_w,
							cd_material_w,
							cd_local_estoque_w,
							trunc(clock_timestamp(),'mm'));

	qt_disponivel_w	:= obter_quantidade_convertida(	cd_material_w,
							qt_disp_estoque_w,
							cd_unidade_medida_w,
							'UMC');

	update	solic_kit_mat_comp
	set	qt_disponivel = qt_disponivel_w
	where	nr_seq_solic_kit = nr_sequencia_p
	and	cd_material = cd_material_w
	and	nr_seq_item_kit = nr_seq_item_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
