SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_reagente_lote_fornec ( nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_marca_w	varchar(255);
nr_lote_w	bigint;
nr_seq_lab_reagente_w	bigint;


BEGIN

select 	max(nr_sequencia || nr_digito_verif),
	max(obter_desc_marca(nr_seq_marca))
into STRICT 	nr_lote_w,
	ds_marca_w
from 	material_lote_fornec
where 	nr_sequencia = nr_seq_lote_p;

select 	max(nr_sequencia)
into STRICT 	nr_seq_lab_reagente_w
from 	LAB_REAGENTE
where 	ds_marca = ds_marca_w;


if (nr_seq_lab_reagente_w > 0) then
	begin

	insert into LAB_REAGENTE_LOTE(dt_inicio_lote,
				dt_atualizacao,
				nr_lote,
				nr_sequencia,
				nm_usuario,
				nr_seq_reagente)
			values (clock_timestamp(),
				clock_timestamp(),
				nr_lote_w,
				nextval('lab_reagente_lote_seq'),
				nm_usuario_p,
				nr_seq_lab_reagente_w);

	end;
elsif (ds_marca_w IS NOT NULL AND ds_marca_w::text <> '') then
	begin

		select nextval('lab_reagente_seq')
		into STRICT nr_seq_lab_reagente_w
		;

		insert into LAB_REAGENTE(nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					ds_marca,
					nr_lote)
				values (nr_seq_lab_reagente_w,
					clock_timestamp(),
					nm_usuario_p,
					ds_marca_w,
					nr_lote_w);
		end;

		insert into LAB_REAGENTE_LOTE(dt_inicio_lote,
						dt_atualizacao,
						nr_lote,
						nr_sequencia,
						nm_usuario,
						nr_seq_reagente)
					values (clock_timestamp(),
						clock_timestamp(),
						nr_lote_w,
						nextval('lab_reagente_lote_seq'),
						nm_usuario_p,
						nr_seq_lab_reagente_w);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
