CREATE OR REPLACE PROCEDURE gerar_realiz_os_ativ_proj_migr ( nr_seq_projeto_p bigint, nr_seq_os_projeto_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_cronograma_w	bigint;
nr_seq_atividade_w		bigint;
qt_min_atividade_w		bigint;
qt_horas_projeto_w		double precision;
nr_seq_atividade_ww	bigint;

c01 CURSOR FOR
SELECT	nr_seq_proj_cron_etapa,
	qt_minuto
from	man_ordem_serv_ativ
where	nr_seq_ordem_serv = nr_seq_os_projeto_p
order by
	nr_seq_proj_cron_etapa;


BEGIN
if (nr_seq_projeto_p IS NOT NULL AND nr_seq_projeto_p::text <> '') and (nr_seq_os_projeto_p IS NOT NULL AND nr_seq_os_projeto_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_cronograma_w
	from	proj_cronograma
	where	nr_seq_proj = nr_seq_projeto_p;

	if (nr_seq_cronograma_w > 0) then
		begin
		open c01;
		loop
		fetch c01 into	nr_seq_atividade_w,
				qt_min_atividade_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			qt_horas_projeto_w := round((dividir(qt_min_atividade_w,60))::numeric,2);

			update	proj_cron_etapa
			set	qt_hora_real = coalesce(qt_hora_real,0) + qt_horas_projeto_w
			where	nr_sequencia = nr_seq_atividade_w;

			CALL atualizar_horas_etapa_cron(nr_seq_atividade_w);

			select	coalesce(max(nr_seq_superior),0)
			into STRICT	nr_seq_atividade_ww
			from	proj_cron_etapa
			where	nr_sequencia = nr_seq_atividade_w;

			if (nr_seq_atividade_ww > 0) then
				begin
				CALL atualizar_horas_etapa_cron(nr_seq_atividade_ww);
				end;
			end if;
			end;
		end loop;
		close c01;
		CALL atualizar_total_horas_cron(nr_seq_cronograma_w);
		CALL proj_desenv_atual_perc_cron(nr_seq_cronograma_w, nm_usuario_p);
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
