SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_recebimento_prontuario ( cd_estabelecimento_p bigint, nm_usuario_p text, ds_lista_sequencia_p text ) AS $body$
DECLARE

 
										 
c01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	transf_prontuario 
	where 	obter_se_contido(nr_sequencia, '(' || ds_lista_sequencia_p || ')') = 'S';

 
nr_sequencia_w					bigint;
ie_transferir_solicitacao_w		varchar(1);
ie_permite_receb_w		varchar(1);
nr_seq_lote_transf_w		bigint;
cd_pf_destino_w			varchar(10);	
ie_usuario_dest_w		varchar(1);

BEGIN
 
ie_transferir_solicitacao_w := obter_param_usuario(941, 53, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_transferir_solicitacao_w);
ie_usuario_dest_w := obter_param_usuario(941, 219, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_usuario_dest_w);
	 
open c01;
loop 
fetch c01 into	 
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	if (ie_usuario_dest_w = 'S') then 
		select	max(cd_pessoa_destino), 
			max(NR_SEQ_LOTE_TRANSF) 
		into STRICT	cd_pf_destino_w, 
			nr_seq_lote_transf_w 
		from	transf_prontuario 
		where	nr_sequencia = nr_sequencia_w;
		 
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
		into STRICT	ie_permite_receb_w 
		from	usuario 
		where	cd_pessoa_fisica = cd_pf_destino_w 
		and	nm_usuario = nm_usuario_p;
	 
		if (ie_permite_receb_w = 'N') and (coalesce(nr_seq_lote_transf_w::text, '') = '') then 
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(242284);				
		end if;
	end if;
	 
	if (ie_transferir_solicitacao_w = 'S') then 
		CALL transf_pront_set_itens(nr_sequencia_w, nm_usuario_p, cd_estabelecimento_p);
	else 
		CALL transferir_prontuario_setores(nr_sequencia_w, nm_usuario_p, '2', cd_estabelecimento_p);
	end if;
			 
	end;
end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
