CREATE OR REPLACE PROCEDURE gerar_recebimento_setor (nm_usuario_p text, nr_seq_atendimento_p bigint, nr_seq_material_p bigint) AS $body$
DECLARE


nr_agrupamento_w		integer;
qt_medic_nao_atend_w	bigint;
								

BEGIN
if (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '')	then
	
	select 	max(nr_agrupamento)
	into STRICT	nr_agrupamento_w
	from	paciente_atend_medic
	where	nr_seq_atendimento	=	nr_seq_atendimento_p
	and		nr_seq_material		= 	nr_seq_material_p;

	if (coalesce(nr_agrupamento_w,0) > 0) then
		update	paciente_atend_medic
		set 	dt_recebimento_medic 	= 	clock_timestamp() ,
				nm_usuario_receb 		=	nm_usuario_p
		where 	nr_seq_atendimento		=	nr_seq_atendimento_p
		and (nr_agrupamento			=	nr_agrupamento_w);
	end if;
	
	select 	count(1)
	into STRICT	qt_medic_nao_atend_w
	from	paciente_atend_medic
	where	nr_seq_atendimento 		=	nr_seq_atendimento_p
	and		dt_recebimento_medic 	is 	null;
	
	if (qt_medic_nao_atend_w = 0) then
		update	paciente_atendimento
		set 	dt_recebimento_medic 	= 	clock_timestamp() ,
				nm_usuario_receb 		=	nm_usuario_p
		where 	nr_seq_atendimento		=	nr_seq_atendimento_p;		
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
