CREATE OR REPLACE PROCEDURE gerar_receb_caixa_boleto ( nr_seq_saldo_caixa_p bigint, nr_titulo_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_caixa_rec_w		bigint;
cd_pessoa_fisica_w		varchar(10);
cd_cgc_w			varchar(14);
nr_seq_trans_boleto_caixa_w	bigint;
ds_msg_retorno_w		varchar(4000);	
 

BEGIN 
 
if (nr_seq_saldo_caixa_p IS NOT NULL AND nr_seq_saldo_caixa_p::text <> '') and (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then 
	 
	select	max(a.nr_sequencia) 
	into STRICT	nr_seq_caixa_rec_w 
	from	caixa_receb a 
	where	a.nr_seq_saldo_caixa	= nr_seq_saldo_caixa_p 
	and	a.nm_usuario		= nm_usuario_p 
	and	coalesce(a.dt_fechamento::text, '') = '';
	 
	/* Se ainda não há recebimento aberto deve gerar um novo recebimento */
 
	if (coalesce(nr_seq_caixa_rec_w::text, '') = '') then 
		select	a.cd_pessoa_fisica, 
			a.cd_cgc 
		into STRICT	cd_pessoa_fisica_w, 
			cd_cgc_w 
		from	titulo_receber a 
		where	a.nr_titulo	= nr_titulo_p;
		 
		select	max(a.nr_seq_trans_boleto_caixa) 
		into STRICT	nr_seq_trans_boleto_caixa_w 
		from	parametro_contas_receber a 
		where	a.cd_estabelecimento	= cd_estabelecimento_p;
		 
		if (coalesce(nr_seq_trans_boleto_caixa_w::text, '') = '') then 
			/*r.aise_application_error(-20011,'Não foi informada a transação padrão para geração do recebimento!' || chr(13) || chr(10) || 
							'Verifique na função Parâmetros Contas a Receber, pasta Tesouraria, ' || 
							'campo Transação recebimento boleto.#@#@');*/
 
			CALL wheb_mensagem_pck.exibir_mensagem_abort(267351);				
		end if;
	 
		select	nextval('caixa_receb_seq') 
		into STRICT	nr_seq_caixa_rec_w 
		;
		 
		insert into caixa_receb(nr_sequencia, 
			nm_usuario, 
			dt_atualizacao, 
			nm_usuario_nrec, 
			dt_atualizacao_nrec, 
			nr_seq_saldo_caixa, 
			nr_seq_trans_financ, 
			dt_recebimento, 
			ie_tipo_receb, 
			vl_especie, 
			cd_pessoa_fisica, 
			cd_cgc) 
		values (nr_seq_caixa_rec_w, 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nr_seq_saldo_caixa_p, 
			nr_seq_trans_boleto_caixa_w, 
			clock_timestamp(), 
			'R', 
			0, 
			cd_pessoa_fisica_w, 
			cd_cgc_w);
	end if;
	 
	ds_msg_retorno_w := baixar_titulos_caixa_rec(nr_seq_caixa_rec_w, ' ' || nr_titulo_p || ' ', null, nm_usuario_p, ds_msg_retorno_w);
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
