CREATE OR REPLACE PROCEDURE gerar_receb_convenio (cd_estabelecimento_p bigint, nr_conta_convenio_p bigint, cd_convenio_p bigint, nr_seq_conta_banco_p bigint, nr_seq_trans_fin_p bigint, vl_recebimento_p bigint, dt_recebimento_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_receb_w	bigint;
cd_convenio_w	bigint;


BEGIN 
 
select 	max(a.cd_convenio_parametro) 
into STRICT 	cd_convenio_w 
from 	conta_paciente a 
where	a.nr_conta_convenio	= nr_conta_convenio_p;
 
select	nextval('convenio_receb_seq') 
into STRICT	nr_seq_receb_w
;
 
insert	into convenio_receb(NR_SEQUENCIA, 
	CD_CONVENIO, 
	DT_RECEBIMENTO, 
	VL_RECEBIMENTO, 
	DT_ATUALIZACAO, 
	NM_USUARIO, 
	IE_STATUS, 
	NR_SEQ_CONTA_BANCO, 
	NR_SEQ_TRANS_FIN, 
	NR_LOTE_CONTABIL, 
	DS_OBSERVACAO, 
	VL_DESPESA_BANCARIA, 
	VL_DEPOSITO, 
	DT_FLUXO_CAIXA, 
	CD_ESTABELECIMENTO, 
	DT_LIBERACAO, 
	IE_INTEGRAR_CB_FLUXO, 
	IE_TIPO_GLOSA, 
	NR_ADIANTAMENTO) 
values (nr_seq_receb_w, 
	coalesce(cd_convenio_w,cd_convenio_p), 
	dt_recebimento_p, 
	vl_recebimento_p, 
	clock_timestamp(), 
	nm_usuario_p, 
	'N', 
	nr_seq_conta_banco_p, 
	nr_seq_trans_fin_p , 
	null, 
	null, 
	null, 
	null, 
	null, 
	cd_estabelecimento_p, 
	null, 
	'N', 
	'N', 
	null);
 
commit;
 
end;
