CREATE OR REPLACE PROCEDURE gerar_receb_ops_vl_pendente ( nr_seq_caixa_p bigint, nr_seq_trans_financ_p bigint, ds_lista_titulo_p text, cd_estabelecimento_p bigint, nr_seq_pagador_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_saldo_w		bigint;
nr_seq_caixa_rec_w	bigint;
cd_pessoa_fisica_w	varchar(10);
cd_cgc_w		varchar(14);	
ds_msg_retorno_w		varchar(4000);				
 

BEGIN 
 
if (coalesce(nr_seq_caixa_p::text, '') = '') then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(188951);
elsif (coalesce(nr_seq_trans_financ_p::text, '') = '') then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(188952);
elsif (coalesce(ds_lista_titulo_p::text, '') = '') then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(188954);
else 
	select	max(a.nr_sequencia) 
	into STRICT	nr_seq_saldo_w 
	from	caixa_saldo_diario a 
	where	a.nr_seq_caixa	= nr_seq_caixa_p 
	and	trunc(a.dt_saldo,'dd') = trunc(clock_timestamp(),'dd') 
	and	coalesce(a.dt_fechamento::text, '') = '';
	 
	if (nr_seq_saldo_w IS NOT NULL AND nr_seq_saldo_w::text <> '') then 
		select	nextval('caixa_receb_seq') 
		into STRICT	nr_seq_caixa_rec_w 
		;
		 
		select	cd_pessoa_fisica, 
			cd_cgc 
		into STRICT	cd_pessoa_fisica_w, 
			cd_cgc_w 
		from	pls_contrato_pagador a 
		where	a.nr_sequencia	= nr_seq_pagador_p;
		 
		insert into caixa_receb(nr_sequencia, 
			nm_usuario, 
			dt_atualizacao, 
			nm_usuario_nrec, 
			dt_atualizacao_nrec, 
			nr_seq_saldo_caixa, 
			vl_especie, 
			nr_seq_trans_financ, 
			cd_pessoa_fisica, 
			cd_cgc, 
			ie_tipo_receb, 
			ds_observacao, 
			dt_recebimento) 
		values (nr_seq_caixa_rec_w, 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nr_seq_saldo_w, 
			0, 
			nr_seq_trans_financ_p, 
			cd_pessoa_fisica_w, 
			cd_cgc_w, 
			'R', 
			obter_desc_expressao(344997), 
			clock_timestamp());
			 
		ds_msg_retorno_w := baixar_titulos_caixa_rec(nr_seq_caixa_rec_w, ds_lista_titulo_p, null, nm_usuario_p, ds_msg_retorno_w);
	else	 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(188955);
	end if;
end if;
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
