CREATE OR REPLACE PROCEDURE gerar_receb_rapido_laudo ( nr_laudo_p bigint, nr_atendimento_p bigint, cd_setor_origem_p bigint, nm_usuario_p text) AS $body$
DECLARE

					 
nr_seq_laudo_w		bigint;
cd_pessoa_usuario_w	varchar(10);
nr_seq_loc_w		bigint;

 

BEGIN 
 
select	coalesce(max(a.nr_sequencia),0) 
into STRICT	nr_seq_laudo_w 
from	laudo_paciente a 
where	nr_laudo = nr_laudo_p 
and	nr_atendimento = nr_atendimento_p;
 
select	a.cd_pessoa_fisica 
into STRICT	cd_pessoa_usuario_w 
from	atendimento_paciente a 
where 	a.nr_atendimento = nr_atendimento_p;
 
select coalesce(max(nr_seq_loc),0) + 1 
into STRICT	nr_seq_loc_w 
from	laudo_paciente_loc 
where	nr_sequencia = nr_seq_laudo_w;
 
insert	into laudo_paciente_loc( 
	nr_seq_loc, 
	cd_pessoa_fisica, 
	cd_setor_registro, 
	ie_com_protocolo, 
	ie_tipo_local, 
	nm_usuario, 
	nm_usuario_nrec, 
	nr_sequencia, 
	dt_atualizacao, 
	dt_atualizacao_nrec, 
	dt_retirada 
	) 
	values ( 
	nr_seq_loc_w, 
	cd_pessoa_usuario_w, 
	cd_setor_origem_p, 
	'N', 
	'1', 
	nm_usuario_p, 
	nm_usuario_p, 
	nr_seq_laudo_w, 
	clock_timestamp(), 
	clock_timestamp(), 
	clock_timestamp());
 
CALL Atualizar_status_exec_proced(nr_seq_laudo_w,nm_usuario_p,'N');	
 
commit;
 
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
