CREATE OR REPLACE PROCEDURE gerar_recist_localizacao ( nr_sequencia_p bigint, nr_sequencia_registro_p bigint, dslocalizacao_p text, mensurar_p text, tipo_lesao_p text, nm_usuario_p text) AS $body$
DECLARE

nr_seq_w  bigint;


BEGIN

	if (nr_sequencia_p = 0) then
		begin

		select	nextval('recist_localizacao_seq')
		into STRICT	nr_seq_w
		;

		Insert into RECIST_LOCALIZACAO(
			nr_sequencia,
			nr_seq_registro,
			ds_localizacao,
			ie_mensurar,
			ie_tipo_lesao,
			nm_usuario,
			nm_usuario_nrec,
			dt_atualizacao,
			dt_atualizacao_nrec,
			ie_mensurar_old)
		values (
			nr_seq_w,
			nr_sequencia_registro_p,
			dslocalizacao_p,
			mensurar_p,
			tipo_lesao_p,
			nm_usuario_p,
			nm_usuario_p,
			clock_timestamp(),
			clock_timestamp(),
			'S');
		end;
	else
		begin
		UPDATE RECIST_LOCALIZACAO
		SET dt_atualizacao = clock_timestamp(),
			DS_LOCALIZACAO = substr(dslocalizacao_p,1,100),
			IE_MENSURAR = substr(mensurar_p,1,1),
			ie_tipo_lesao = substr(tipo_lesao_p,1,2)
		WHERE nr_sequencia  = nr_sequencia_p;
		end;
	end if;


commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
