CREATE OR REPLACE PROCEDURE gerar_recist_medicao ( nr_sequencia_p bigint, nr_seq_registro_p bigint, nr_seq_localizacao_p bigint, dt_exame_p timestamp, dt_registro_p timestamp, qt_diametro_x_p bigint, qt_diametro_y_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_w  bigint;

BEGIN

	if (nr_sequencia_p = 0) then
		begin

		select	nextval('recist_reg_localizacao_seq')
		into STRICT	nr_seq_w
		;

		Insert into RECIST_REG_LOCALIZACAO(
				nr_sequencia,
				nr_seq_registro,
				nr_seq_localizacao,
				dt_registro,
				DT_EXAME,
				qt_diametro_x,
				qt_diametro_y,
				nm_usuario,
				nm_usuario_nrec,
				dt_atualizacao,
				dt_atualizacao_nrec)
		values (
				nr_seq_w,
				nr_seq_registro_p,
				nr_seq_localizacao_p,
				dt_registro_p,
				dt_exame_p,
				qt_diametro_x_p,
				qt_diametro_y_p,
				nm_usuario_p,
				nm_usuario_p,
				clock_timestamp(),
				clock_timestamp());
			end;
		else
			begin
			UPDATE RECIST_REG_LOCALIZACAO
			SET dt_atualizacao = clock_timestamp(),
				qt_diametro_x = qt_diametro_x_p,
				qt_diametro_y = qt_diametro_y_p,
				dt_registro = dt_registro_p,
				dt_exame    = dt_exame_p
			WHERE nr_sequencia  = nr_sequencia_p;
			end;
	end if;

commit;
end;
