CREATE OR REPLACE PROCEDURE gerar_recomendacoes_protocolo ( cd_protocolo_p bigint, nr_sequencia_p bigint, nr_seq_classif_p bigint, ds_lista_recomendacoes_p text, nm_usuario_p text) AS $body$
DECLARE


ds_lista_recomendacoes_w	varchar(2000);
nr_pos_virgula_w		bigint;
cd_tipo_recomendacao_w		bigint;
nr_seq_rec_w			integer;


BEGIN
if (ds_lista_recomendacoes_p IS NOT NULL AND ds_lista_recomendacoes_p::text <> '') and (cd_protocolo_p IS NOT NULL AND cd_protocolo_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nr_seq_classif_p IS NOT NULL AND nr_seq_classif_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	ds_lista_recomendacoes_w	:= ds_lista_recomendacoes_p;

	while(ds_lista_recomendacoes_w IS NOT NULL AND ds_lista_recomendacoes_w::text <> '') loop
		begin
		nr_pos_virgula_w	:= position(',' in ds_lista_recomendacoes_w);
		if (nr_pos_virgula_w > 0) then
			begin
			cd_tipo_recomendacao_w		:= substr(ds_lista_recomendacoes_w,0,nr_pos_virgula_w-1);
			ds_lista_recomendacoes_w	:= substr(ds_lista_recomendacoes_w,nr_pos_virgula_w+1,length(ds_lista_recomendacoes_w));
			end;
		else
			begin
			cd_tipo_recomendacao_w		:= (coalesce(ds_lista_recomendacoes_w,0))::numeric;
			ds_lista_recomendacoes_w	:= null;
			end;
		end if;

		if (coalesce(cd_tipo_recomendacao_w,0) > 0) then
			begin

			select (coalesce(max(nr_seq_rec),0) + 1)
			into STRICT	nr_seq_rec_w
			from	protocolo_medic_rec
			where	cd_protocolo = cd_protocolo_p
			and	nr_sequencia = nr_sequencia_p;

			insert into protocolo_medic_rec(
						cd_protocolo,
						nr_sequencia,
						nr_seq_rec,
						dt_atualizacao,
						nm_usuario,
						cd_recomendacao,
						nr_seq_classif)
						values (
						cd_protocolo_p,
						nr_sequencia_p,
						nr_seq_rec_w,
						clock_timestamp(),
						nm_usuario_p,
						cd_tipo_recomendacao_w,
						nr_seq_classif_p);
			end;
		end if;
		end;
	end loop;
	end;
end if;
commit;
end;
$body$
