SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_ref_result_lab_ext ( nr_seq_result_item_p bigint, nr_seq_resultado_p bigint) AS $body$
DECLARE


qt_dias_w	bigint	:= 0;
qt_minima_w	double precision;
qt_maxima_w	double precision;
pr_minimo_w	double precision;
pr_maximo_w	double precision;
ds_observacao_w	varchar(2000);
ds_refer_w	exame_lab_padrao.ds_observacao%type;
ie_sexo_w	varchar(1);
cd_pessoa_fisica_w	varchar(10);
dt_nascimento_w	timestamp;
nr_seq_exame_w	bigint;
nr_seq_material_w	bigint;
qt_horas_w	double precision := 0;
qt_casas_decimais_dias_w	bigint := 2;
cd_estabelecimento_w		prescr_medica.cd_estabelecimento%type;


BEGIN

if (nr_seq_result_item_p IS NOT NULL AND nr_seq_result_item_p::text <> '') and (nr_seq_resultado_p IS NOT NULL AND nr_seq_resultado_p::text <> '') then

	select	max(cd_pessoa_fisica)
	into STRICT	cd_pessoa_fisica_w
	from 	exame_lab_resultado
	where 	nr_seq_resultado = nr_seq_resultado_p;



	select	max(nr_seq_material),
		max(nr_seq_exame)
	into STRICT	nr_seq_material_w,
		nr_seq_exame_w
	from	exame_lab_result_item
	where	nr_sequencia = nr_seq_result_item_p
	and	nr_seq_resultado = nr_seq_resultado_p;

	select	coalesce(max(a.cd_estabelecimento),0)
	into STRICT	cd_estabelecimento_w
	from	prescr_medica a,
		exame_lab_resultado b
	where	a.nr_prescricao = b.nr_prescricao
	  and 	b.nr_seq_resultado = nr_seq_resultado_p;

	select	CASE WHEN coalesce(max(ie_idade_int_val_ref), 'N')='N' THEN  2  ELSE 0 END
	into STRICT	qt_casas_decimais_dias_w
	from 	lab_parametro
	where 	cd_estabelecimento = cd_estabelecimento_w;

	if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') and (nr_seq_exame_w IS NOT NULL AND nr_seq_exame_w::text <> '') then
		select 	dt_nascimento,
			ie_sexo
		into STRICT 	dt_nascimento_w,
			ie_sexo_w
		from 	pessoa_fisica
		where 	cd_pessoa_fisica = cd_pessoa_fisica_w;

		qt_dias_w  := obter_dias_entre_datas(dt_nascimento_w,clock_timestamp());
		qt_horas_w := round((Obter_Hora_Entre_datas(dt_nascimento_w,clock_timestamp()))::numeric,5);

		select 	max(qt_minima),
			max(qt_maxima),
			max(qt_percent_min),
			max(qt_percent_max),
			max(ds_observacao)
		into STRICT	qt_minima_w,
			qt_maxima_w,
			pr_minimo_w,
			pr_maximo_w,
			ds_observacao_w
		from (
			SELECT 	qt_minima,
				qt_maxima,
				qt_percent_min,
				qt_percent_max,
				ds_observacao
			from 	exame_lab_padrao
			where 	((ie_sexo = ie_sexo_w) or (ie_sexo = '0'))
			and 	nr_seq_exame = nr_seq_exame_w
			and 	coalesce(nr_seq_material,nr_seq_material_w) = nr_seq_material_w
			and 	(((trunc((qt_dias_w / 365.25), qt_casas_decimais_dias_w) between qt_idade_min and qt_idade_max) and (ie_periodo = 'A')) or
			((trunc(((qt_dias_w / 365.25) * 12),qt_casas_decimais_dias_w) between qt_idade_min and qt_idade_max) and (ie_periodo = 'M')) or
			(qt_dias_w between qt_idade_min and qt_idade_max AND ie_periodo = 'D') or
			(qt_horas_w between qt_idade_min and qt_idade_max AND ie_periodo = 'H'))
			and 	ie_tipo_valor in (0,3)
			and coalesce(ie_situacao,'A') = 'A'
			order by coalesce(nr_seq_material, 9999999999), coalesce(nr_seq_metodo, 9999999999), ie_sexo, CASE WHEN ie_periodo='D' THEN 1 WHEN ie_periodo='M' THEN 2  ELSE 3 END  LIMIT 1) alias27;


		if (qt_minima_w >= 0) and (qt_maxima_w > 0) then
			ds_refer_w := (qt_minima_w)::numeric  || ' a '|| (qt_maxima_w)::numeric;
		elsif (pr_minimo_w >= 0) and (pr_maximo_w > 0) then
			ds_refer_w := (pr_minimo_w)::numeric  || ' a '|| (pr_maximo_w)::numeric;
		else
			ds_refer_w := ds_observacao_w;
		end if;

		update 	exame_lab_result_item
		set	ds_referencia = ds_refer_w,
			pr_minimo = pr_minimo_w,
			pr_maximo = pr_maximo_w,
			qt_minima = qt_minima_w,
			qt_maxima = qt_maxima_w
		where	nr_sequencia = nr_seq_result_item_p
		and	nr_seq_resultado = nr_seq_resultado_p;

		commit;

	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_ref_result_lab_ext ( nr_seq_result_item_p bigint, nr_seq_resultado_p bigint) FROM PUBLIC;

