CREATE OR REPLACE PROCEDURE gerar_registros_dime ( nr_seq_dime_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_registro_w		fis_regra_dime_reg.cd_registro%type;
ie_gerar_w		varchar(1);
contador_w		bigint	:= 0;

c_registro CURSOR FOR
	SELECT	vl_dominio
	from 	valor_dominio
	where	cd_dominio	= 4650;


BEGIN

delete	from fis_regra_dime_reg
where	nr_seq_regra_dime	= nr_seq_dime_p;

open c_registro;
loop
fetch c_registro into
	cd_registro_w;
EXIT WHEN NOT FOUND; /* apply on c_registro */
	begin

	if (cd_registro_w  (20, 21, 22, 23, 24, 25, 26, 30, 33, 47, 48, 49, 50, 51, 80, 81, 82, 83, 84, 90, 91, 92, 93, 94,98,99)) then
		ie_gerar_w	:= 'S';
	else
		ie_gerar_w	:= 'N';
	end if;

	insert into fis_regra_dime_reg(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_registro,
		ie_gerar,
		nr_seq_regra_dime)
	values (nextval('fis_regra_dime_reg_seq'),
		clock_timestamp(),
		'Tasy',
		clock_timestamp(),
		'Tasy',
		cd_registro_w,
		ie_gerar_w,
		nr_seq_dime_p);

	contador_w	:= contador_w + 1;

	if (contador_w mod 100 = 0) then
		commit;
	end if;

	end;
end loop;
close c_registro;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
