SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_registro_kit_estoque ( nr_seq_pedido_p bigint, nr_seq_agenda_p bigint, cd_estabelecimento_p bigint, cd_pessoa_fisica_p bigint, nm_usuario_p text, cd_local_estoque_p text, nr_sequencia_p INOUT bigint) AS $body$
DECLARE


ie_ordem_w		varchar(1);
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
cd_kit_material_w		integer;
nr_sequencia_w		bigint;
nr_seq_kit_estoque_w	bigint;
ie_gerado_w		varchar(1) := 'N';
ds_erro_w		varchar(255);
nm_paciente_w		varchar(255);

C01 CURSOR FOR
	SELECT	cd_kit_material
	from   	agenda_pac_pedido_kit
	where  	nr_seq_pedido = nr_seq_pedido_p
	order by   cd_kit_material;


BEGIN

select	substr(a.nm_paciente,1,255)
into STRICT	nm_paciente_w
from   	agenda_paciente a
where  	a.nr_sequencia = nr_seq_agenda_p;

select	nextval('kit_estoque_reg_seq')
into STRICT	nr_sequencia_w
;


insert 	into kit_estoque_reg(
			nr_sequencia,
			cd_estabelecimento,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			cd_pessoa_resp,
			dt_liberacao,
			nm_usuario_lib,
			ds_registro_kit,
			dt_utilizacao,
			nm_usuario_util,
			ie_situacao,
			ie_excluido,
			nr_seq_motivo_exclusao,
			nr_seq_pedido_agenda,
			ie_basico,
			ie_origem,
			nr_seq_agenda,
			cd_local_estoque)
	values (	nr_sequencia_w,
			cd_estabelecimento_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			cd_pessoa_fisica_p,
			null,
			null,
			nm_paciente_w,
			null,
			null,
			'A',
			'N',
			null,
			CASE WHEN coalesce(nr_seq_pedido_p,0)=0 THEN ''  ELSE nr_seq_pedido_p END ,
			'N',
			'S',
			nr_seq_agenda_p,
			cd_local_estoque_p);

open C01;
loop
fetch C01 into
	cd_kit_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(nr_seq_pedido_p,0) > 0) then
		begin
		select	nextval('kit_estoque_seq')
		into STRICT	nr_seq_kit_estoque_w
		;

		insert 	into kit_estoque(
				nr_sequencia,
				cd_kit_material,
				dt_atualizacao,
				nm_usuario,
				dt_montagem,
				nm_usuario_montagem,
				dt_utilizacao,
				nm_usuario_util,
				nr_cirurgia,
				cd_estabelecimento,
				cd_medico,
				nr_prescricao,
				nr_atendimento,
				cd_local_estoque,
				nr_seq_reg_kit,
				ie_status)
		values (	nr_seq_kit_estoque_w,
				cd_kit_material_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				null,
				null,
				null,
				cd_estabelecimento_p,
				null,
				null,
				null,
				cd_local_estoque_p,
				nr_sequencia_w,
				null);
		end;
	end if;
	end;
end loop;
close C01;

nr_sequencia_p := nr_sequencia_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
