CREATE OR REPLACE PROCEDURE gerar_registro_pepa ( cd_paciente_p text, nr_atendimento_p bigint, cd_profissional_p text, nr_seq_tipo_reg_p bigint, ds_lista_templates_p text, ds_lista_prontuario_p text, dt_registro_p timestamp, nm_usuario_p text, nr_seq_registro_p INOUT bigint) AS $body$
DECLARE


nr_seq_registro_w	bigint;
ds_lista_templates_w	varchar(2000);
ds_lista_prontuario_w	varchar(2000);
tam_lista_w		bigint;
ie_pos_virgula_w	smallint;
nr_seq_template_w	bigint;
nr_seq_item_pep_w	bigint;
nr_seq_reg_template_w	bigint;
nr_seq_reg_item_w	bigint;
nr_seq_apres_w		integer;


BEGIN
if (cd_paciente_p IS NOT NULL AND cd_paciente_p::text <> '') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (cd_profissional_p IS NOT NULL AND cd_profissional_p::text <> '') and (nr_seq_tipo_reg_p IS NOT NULL AND nr_seq_tipo_reg_p::text <> '') and
	((ds_lista_templates_p IS NOT NULL AND ds_lista_templates_p::text <> '') or (ds_lista_prontuario_p IS NOT NULL AND ds_lista_prontuario_p::text <> '')) and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	select	nextval('ehr_registro_seq')
	into STRICT	nr_seq_registro_w
	;

	insert into ehr_registro(
		nr_sequencia,
		cd_paciente,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_tipo_reg,
		dt_registro,
		cd_profissional,
		nr_atendimento,
		dt_liberacao)
	values (
		nr_seq_registro_w,
		cd_paciente_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_tipo_reg_p,
		dt_registro_p,
		cd_profissional_p,
		nr_atendimento_p,
		null);

	ds_lista_templates_w	:= ds_lista_templates_p;

	while	(ds_lista_templates_w IS NOT NULL AND ds_lista_templates_w::text <> '') loop
		begin
		tam_lista_w	:= length(ds_lista_templates_w);
		ie_pos_virgula_w	:= position(',' in ds_lista_templates_w);

		if (ie_pos_virgula_w <> 0) then
			nr_seq_reg_item_w	:= (substr(ds_lista_templates_w, 1, (ie_pos_virgula_w - 1)))::numeric;
			ds_lista_templates_w	:= substr(ds_lista_templates_w, (ie_pos_virgula_w + 1), tam_lista_w);
		end if;

		if (nr_seq_reg_item_w > 0) then

			select	nr_seq_template,
				nr_seq_apres
			into STRICT	nr_seq_template_w,
				nr_seq_apres_w
			from	ehr_tipo_reg_item
			where	nr_sequencia	= nr_seq_reg_item_w;

			select	nextval('ehr_reg_template_seq')
			into STRICT	nr_seq_reg_template_w
			;

			insert into ehr_reg_template(
				nr_sequencia,
				nr_seq_reg,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				dt_registro,
				nr_seq_template,
				nr_seq_item_pep,
				dt_liberacao,
				nr_seq_apres)
			values (
				nr_seq_reg_template_w,
				nr_seq_registro_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				dt_registro_p,
				nr_seq_template_w,
				null,
				null,
				nr_seq_apres_w);

		end if;
		end;
	end loop;

	ds_lista_prontuario_w	:= ds_lista_prontuario_p;

	while	(ds_lista_prontuario_w IS NOT NULL AND ds_lista_prontuario_w::text <> '') loop
		begin
		tam_lista_w	:= length(ds_lista_prontuario_w);
		ie_pos_virgula_w	:= position(',' in ds_lista_prontuario_w);

		if (ie_pos_virgula_w <> 0) then
			nr_seq_reg_item_w	:= (substr(ds_lista_prontuario_w, 1, (ie_pos_virgula_w - 1)))::numeric;
			ds_lista_prontuario_w	:= substr(ds_lista_prontuario_w, (ie_pos_virgula_w + 1), tam_lista_w);
		end if;

		if (nr_seq_reg_item_w > 0) then

			select	nr_seq_item_pep,
				nr_seq_apres
			into STRICT	nr_seq_item_pep_w,
				nr_seq_apres_w
			from	ehr_tipo_reg_item
			where	nr_sequencia	= nr_seq_reg_item_w;

			select	nextval('ehr_reg_template_seq')
			into STRICT	nr_seq_reg_template_w
			;

			insert into ehr_reg_template(
				nr_sequencia,
				nr_seq_reg,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				dt_registro,
				nr_seq_template,
				nr_seq_item_pep,
				dt_liberacao,
				nr_seq_apres)
			values (
				nr_seq_reg_template_w,
				nr_seq_registro_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				dt_registro_p,
				null,
				nr_seq_item_pep_w,
				null,
				nr_seq_apres_w);

		end if;
		end;
	end loop;

end if;

nr_seq_registro_p	:= nr_seq_registro_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_registro_pepa ( cd_paciente_p text, nr_atendimento_p bigint, cd_profissional_p text, nr_seq_tipo_reg_p bigint, ds_lista_templates_p text, ds_lista_prontuario_p text, dt_registro_p timestamp, nm_usuario_p text, nr_seq_registro_p INOUT bigint) FROM PUBLIC;

