SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_regra_alta_exec_proc (nr_atendimento_p bigint, nr_prescricao_p bigint, cd_setor_atendimento_p bigint, nm_usuario_p text, cd_motivo_alta_p INOUT bigint, ie_gerar_alta_p INOUT text) AS $body$
DECLARE


cd_estabelecimento_w		bigint;
ie_tipo_atendimento_w		smallint;
qt_pendentes_w			bigint;
cd_motivo_alta_w		bigint;
ds_erro_w			varchar(2000);
qt_regra_w			bigint;

c01 CURSOR FOR
	SELECT	cd_motivo_alta
	from	REGRA_ALTA_EXEC_PROC
	where	cd_setor_atendimento	= cd_setor_atendimento_p
	and	ie_tipo_atendimento	= ie_tipo_atendimento_w;


BEGIN

ie_gerar_alta_p		:= 'N';

select	count(*)
into STRICT	qt_regra_w
from	REGRA_ALTA_EXEC_PROC;

if (qt_regra_w > 0) then
	begin

	select	max(cd_estabelecimento),
		max(ie_tipo_atendimento)
	into STRICT	cd_estabelecimento_w,
		ie_tipo_atendimento_w
	from	atendimento_paciente
	where	nr_atendimento		= nr_atendimento_p
	and 	coalesce(dt_alta::text, '') = '';


	open	c01;
	loop
	fetch	c01 into
		cd_motivo_alta_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		select	count(*)
		into STRICT	qt_pendentes_w
		from	prescr_procedimento a,
			prescr_medica b
		where	a.nr_prescricao		= b.nr_prescricao
		and	b.nr_atendimento	= nr_atendimento_p
		and 	(coalesce(b.dt_liberacao, b.dt_liberacao_medico) IS NOT NULL AND (coalesce(b.dt_liberacao, b.dt_liberacao_medico))::text <> '')
		and	a.cd_motivo_baixa	= 0
		and	coalesce(a.dt_baixa::text, '') = ''
		and             coalesce(a.ie_suspenso,'N') = 'N';

		if (qt_pendentes_w 	= 0) then
			ie_gerar_alta_p		:= 'S';
			cd_motivo_alta_p	:= cd_motivo_alta_w;
		end if;


		end;
	end loop;
	close c01;

	commit;

	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_regra_alta_exec_proc (nr_atendimento_p bigint, nr_prescricao_p bigint, cd_setor_atendimento_p bigint, nm_usuario_p text, cd_motivo_alta_p INOUT bigint, ie_gerar_alta_p INOUT text) FROM PUBLIC;

