CREATE OR REPLACE PROCEDURE gerar_regra_desc_repasse ( nr_repasse_terceiro_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_referencia_w		timestamp;
tx_desconto_w		double precision;
vl_desconto_w		double precision;
nr_sequencia_item_w	bigint;


BEGIN

select	max(dt_mesano_referencia)
into STRICT	dt_referencia_w
from	repasse_terceiro
where	nr_repasse_terceiro		= nr_repasse_terceiro_p;

select	max(tx_desconto)
into STRICT	tx_desconto_w
from	regra_desc_repasse
where	trunc(dt_mesano_ref,'month')	= trunc(dt_referencia_w, 'month');

if (coalesce(tx_desconto_w, 0) > 0) then

	vl_desconto_w	:= dividir_sem_round((obter_valor_repasse(nr_repasse_terceiro_p, 'R') * tx_desconto_w)::numeric, 100);

	select	coalesce(max(nr_sequencia_item),0)
	into STRICT	nr_sequencia_item_w
	from	repasse_terceiro_item
	where	nr_repasse_terceiro	= nr_repasse_terceiro_p;

	insert into repasse_terceiro_item(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		vl_repasse,
		nr_repasse_terceiro,
		nr_sequencia_item)
	values (nextval('repasse_terceiro_item_seq'),
		clock_timestamp(),
		nm_usuario_p,
		coalesce(vl_desconto_w,0),
		nr_repasse_terceiro_p,
		nr_sequencia_item_w + 1);

end if;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
