CREATE OR REPLACE PROCEDURE gerar_regra_envio_email_filtro () AS $body$
DECLARE


nr_seq_regra_w			bigint;
ie_consignado_w			varchar(1);

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.ie_consignado
from	regra_envio_email_compra a
where not exists (
	SELECT	1
	from	envio_email_compra_filtro x
	where	a.nr_sequencia = x.nr_seq_regra);


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_regra_w,
	ie_consignado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	insert into envio_email_compra_filtro(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_regra,
		ie_consignado,
		ie_envia_email)
	values (	nextval('envio_email_compra_filtro_seq'),
		clock_timestamp(),
		'Tasy',
		clock_timestamp(),
		'Tasy',
		nr_seq_regra_w,
		ie_consignado_w,
		'S');

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
