CREATE OR REPLACE PROCEDURE gerar_regra_finalizacao_pep ( nr_seq_senha_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ie_regra_p text) AS $body$
DECLARE



qt_regra_w		bigint;
qt_senha_finalizada_w	bigint;


BEGIN
	if (ie_regra_p IS NOT NULL AND ie_regra_p::text <> '') then
		select count(*)
		into STRICT	qt_regra_w
		from 	regra_finaliza_senha
		where	ie_regra = ie_regra_p;
	end if;

	if (qt_regra_w > 0) then
		if (coalesce(nr_seq_senha_p,0) > 0) then

			select	count(*)
			into STRICT	qt_senha_finalizada_w
			from	paciente_senha_fila
			where	nr_sequencia = nr_seq_senha_p
			and	coalesce(dt_fim_atendimento::text, '') = '';

			if (coalesce(qt_senha_finalizada_w,0) > 0) then
				CALL iniciar_finalizar_atend_senha(nr_seq_senha_p,'F',nm_usuario_p,cd_estabelecimento_p,'S');
			end if;

		end if;
		commit;
	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
