CREATE OR REPLACE PROCEDURE gerar_regra_finalizacao ( nr_seq_senha_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ie_regra_p text) AS $body$
DECLARE

 
					 
qt_regra_w		bigint;


BEGIN 
	if (ie_regra_p IS NOT NULL AND ie_regra_p::text <> '') then 
		select count(*) 
		into STRICT	qt_regra_w 
		from 	regra_finaliza_senha 
		where	ie_regra = ie_regra_p;	
	end if;
	 
	if (qt_regra_w > 0) then 
		CALL finalizar_senha_se_pendente(nr_seq_senha_p, nm_usuario_p, cd_estabelecimento_p,'S');	
		commit;
	end if;
 
end;
$body$
LANGUAGE PLPGSQL
