SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_reg_aux_isbt (nr_bolsa_p text, nr_seq_horario_p bigint, nm_usuario_P text) AS $body$
DECLARE

                        
    nr_sequencia_w      bigint;
    nr_bolsa_w  bigint;


BEGIN
    select  count(*)
    into STRICT    nr_bolsa_w
    from    prescr_proc_bol_aux
    where   nr_seq_horario = nr_seq_horario_p
    and     nr_bolsa = nr_bolsa_p;

    if (nr_bolsa_w > 0 ) then
    
        update  prescr_proc_bol_aux
        set     ie_situacao = 'A'
        where   nr_seq_horario = nr_seq_horario_p
        and     nr_bolsa = nr_bolsa_p
        and     ie_situacao = 'I';
    
    else 
        select	nextval('prescr_solucao_evento_seq')
        into STRICT	nr_sequencia_w
;

        insert into prescr_proc_bol_aux(nr_sequencia,
            nr_seq_horario,
            dt_atualizacao_nrec,
            DT_ATUALIZACAO,
            nr_bolsa,
            nm_usuario_nrec,
            nm_usuario,
            ie_situacao)
        values ( nr_sequencia_w,
            nr_seq_horario_p,
            clock_timestamp(),
            clock_timestamp(),
            nr_bolsa_p,
            nm_usuario_P,
            nm_usuario_P,
            'A');
    end if;
    commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
