CREATE OR REPLACE PROCEDURE gerar_reg_product_dic ( nr_seq_product_requirement_p bigint, nr_seq_objeto_p bigint, ie_tipo_objeto_p text, nm_usuario_p text, cd_funcao_p bigint default null) AS $body$
DECLARE


nr_seq_pd_w			reg_product_dic.nr_sequencia%type;
nr_seq_reg_funcao_pr_w		reg_product_dic.nr_seq_reg_funcao_pr%type;


BEGIN

begin
select	nr_sequencia
into STRICT	nr_seq_pd_w
from	reg_product_dic
where	nr_seq_product_requirement = nr_seq_product_requirement_p
and	nr_seq_objeto = nr_seq_objeto_p
and	ie_tipo_objeto = ie_tipo_objeto_p  LIMIT 1;
exception
when others then
	nr_seq_pd_w	:=	0;
end;

if (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_reg_funcao_pr_w
	from	reg_funcao_pr
	where	cd_funcao = cd_funcao_p
	and	nr_seq_product_req = nr_seq_product_requirement_p;
end if;

if (nr_seq_pd_w = 0) then
	insert into reg_product_dic(
		nr_sequencia,
		nr_seq_product_requirement,
		nr_seq_objeto,
		nr_seq_reg_funcao_pr,
		ie_tipo_objeto,
		nm_usuario,
		dt_atualizacao )
	values (	nextval('reg_product_dic_seq'),
		nr_seq_product_requirement_p,
		nr_seq_objeto_p,
		nr_seq_reg_funcao_pr_w,
		ie_tipo_objeto_p,
		nm_usuario_p,
		clock_timestamp() );
end if;

commit;

end;
$body$
