CREATE OR REPLACE PROCEDURE gerar_reincidencia_problema ( nr_seq_problema_p bigint, nr_atendimento_p bigint) AS $body$
DECLARE

 
nr_atendimento_w	bigint;
cd_pessoa_fisica_w	varchar(10);
cd_profissional_w	varchar(10);


BEGIN 
 
if (nr_seq_problema_p IS NOT NULL AND nr_seq_problema_p::text <> '') then 
	select	coalesce(nr_atendimento_p, a.nr_atendimento), 
		a.cd_pessoa_fisica, 
		obter_pf_usuario(wheb_usuario_pck.get_nm_usuario,'C') 
	into STRICT	nr_atendimento_w, 
		cd_pessoa_fisica_w, 
		cd_profissional_w 
	from	lista_problema_pac a 
	where	a.nr_sequencia = nr_seq_problema_p;
 
	if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '' AND cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then 
	insert into lista_problema_pac_incid( 
		nr_sequencia, 
		dt_atualizacao, 
		nm_usuario, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		cd_pessoa_fisica, 
		nr_atendimento, 
		nr_seq_problema, 
		cd_profissional) 
	values (	nextval('lista_problema_pac_incid_seq'), 
		clock_timestamp(), 
		wheb_usuario_pck.get_nm_usuario, 
		clock_timestamp(), 
		wheb_usuario_pck.get_nm_usuario, 
		cd_pessoa_fisica_w, 
		nr_atendimento_w, 
		nr_seq_problema_p, 
		cd_profissional_w);
	end if;
end if;
 
commit;
 
end;
$body$
