CREATE OR REPLACE PROCEDURE gerar_relat_fluxo_passado_real (cd_estabelecimento_p bigint, cd_empresa_p bigint, ie_restringe_estab_p text, dt_mes_ref_p timestamp, dt_atual_p timestamp, nm_usuario_p text, qt_nivel_p bigint, ie_classificacao_p text, vl_saldo_anterior_p bigint, ie_data_atual_p text, ie_todas_contas_p text) AS $body$
DECLARE


dt_atual_w		timestamp;

-- 'RPR' = Relat√≥rio com passado/a realizar do dia separados
BEGIN

dt_atual_w		:= null;

if (ie_classificacao_p in ('PR','PRG')) then	-- passado/realizar
	if (ie_data_atual_p = 'R') then
		dt_atual_w	:= coalesce(dt_atual_p, clock_timestamp());
	else
		dt_atual_w	:= coalesce(dt_atual_p, clock_timestamp()) + 1;
	end if;

end if;

CALL gerar_view_fluxo_caixa(	cd_estabelecimento_p,
			trunc(dt_mes_ref_p,'month'),
			fim_mes(dt_mes_ref_p),
			dt_atual_w,
			'D',
			'RPR',
			qt_nivel_p,
			cd_empresa_p,
			ie_restringe_estab_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
