CREATE OR REPLACE PROCEDURE gerar_relat_telemedicina ( nm_usuario_p text, dt_inicial_p timestamp, dt_final_p timestamp) AS $body$
DECLARE


qt_commit_w	bigint := 0;

C01 CURSOR FOR
	SELECT	a.dt_atualizacao,
		a.nm_usuario,
		a.dt_atualizacao_nrec,
		a.nr_atendimento,
		a.dt_inicio_consulta,
		a.dt_fim_consulta,
		a.nr_seq_agenda,
		a.ds_url_telemedicina
	from	pep_tlc_registros a
	where	a.dt_inicio_consulta between dt_inicial_p and dt_final_p;

BEGIN

delete FROM agenda_pep_consulta_w where nm_usuario = nm_usuario_p;

commit;

for reg_tlc in C01 loop
	begin
	qt_commit_w := qt_commit_w + 1;

	insert into agenda_pep_consulta_w(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_atendimento,
		dt_inicio_consulta,
		dt_fim_consulta,
		nr_seq_agenda,
		ds_url_telemedicina)
	values (
		nextval('agenda_pep_consulta_w_seq'),
		reg_tlc.dt_atualizacao,
		nm_usuario_p,
		reg_tlc.dt_atualizacao_nrec,
		reg_tlc.nm_usuario,
		reg_tlc.nr_atendimento,
		reg_tlc.dt_inicio_consulta,
		reg_tlc.dt_fim_consulta,
		reg_tlc.nr_seq_agenda,
		reg_tlc.ds_url_telemedicina);

	if (qt_commit_w > 500) then
		begin
		qt_commit_w := 0;
		commit;
		end;
	end if;
	end;
end loop;

commit;

end;
$body$
