CREATE OR REPLACE PROCEDURE gerar_rel_nota_fiscal_venc ( nm_usuario_p text, ie_tipo_data_p text, dt_inicio_p timestamp, dt_fim_p timestamp, cd_operacao_p text, cd_estabelecimento_p bigint ) AS $body$
DECLARE


nr_count_w		bigint;
cd_cgc_emitente_w	varchar(14);
ds_razao_social_w		pessoa_juridica.ds_razao_social%type;
nr_nota_fiscal_w		varchar(255);
nr_sequencia_nf_w		bigint;
dt_vencimento_w		varchar(20);
vl_vencimento_w		double precision;
nr_sequencia_w		bigint;
nr_titulo_pagar_w		bigint;

C01 CURSOR FOR
SELECT	a.cd_cgc_emitente,
	b.nr_titulo_pagar,
	c.ds_razao_social,
	a.nr_nota_fiscal,
	a.nr_sequencia_nf,
	to_char(b.dt_vencimento,'dd/mm/yyyy hh24:mi:ss'),
	b.vl_vencimento,
	a.nr_sequencia
from	nota_fiscal_venc b,
	pessoa_juridica c,
	nota_fiscal a
where	c.cd_cgc = a.cd_cgc_emitente
and	a.nr_sequencia = b.nr_sequencia
and	a.ie_situacao  = 1
and	CASE WHEN ie_tipo_data_p='0' THEN b.dt_vencimento WHEN ie_tipo_data_p='1' THEN a.dt_atualizacao WHEN ie_tipo_data_p='2' THEN a.dt_emissao WHEN ie_tipo_data_p='3' THEN a.dt_entrada_saida WHEN ie_tipo_data_p='4' THEN a.dt_atualizacao_estoque END  between dt_inicio_p and fim_dia(dt_fim_p)
and	((a.cd_operacao_nf = cd_operacao_p) or (coalesce(cd_operacao_p,'0') = '0'))
and	a.cd_estabelecimento = cd_estabelecimento_p
order by	b.dt_vencimento;


BEGIN

delete from w_gerar_rel_nf_venc where nm_usuario = nm_usuario_p;

open C01;
loop
fetch C01 into
	cd_cgc_emitente_w,
	nr_titulo_pagar_w,
	ds_razao_social_w,
	nr_nota_fiscal_w,
	nr_sequencia_nf_w,
	dt_vencimento_w,
	vl_vencimento_w,
	nr_sequencia_w;	
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	
	insert into w_gerar_rel_nf_venc(nm_usuario,
			cd_cgc_emitente,
			nr_titulo_pagar,
			ds_razao_social,
			nr_nota_fiscal,
			nr_sequencia_nf,
			dt_vencimento,
			vl_vencimento,
			nr_sequencia)
		values (nm_usuario_p,
			cd_cgc_emitente_w,
			nr_titulo_pagar_w,
			ds_razao_social_w,
			nr_nota_fiscal_w,
			nr_sequencia_nf_w,
			to_date(dt_vencimento_w,'dd/mm/yyyy hh24:mi:ss'),
			vl_vencimento_w,
			nr_sequencia_w);
						
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
